// src/pages/Landing.js
import React from "react";
import {
  Box,
  Button,
  Card,
  Container,
  Grid,
  Paper,
  Stack,
  Typography,
  Chip,
} from "@mui/material";
import { alpha } from "@mui/material/styles";
import VerifiedIcon from "@mui/icons-material/Verified";
import LockIcon from "@mui/icons-material/Lock";
import SpeedIcon from "@mui/icons-material/Speed";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import QueryStatsIcon from "@mui/icons-material/QueryStats";
import SmartphoneIcon from "@mui/icons-material/Smartphone";
import BoltIcon from "@mui/icons-material/Bolt";
import FactCheckIcon from "@mui/icons-material/FactCheck";
import PaidIcon from "@mui/icons-material/Paid";
import SecurityIcon from "@mui/icons-material/Security";
import { useNavigate } from "react-router-dom";

export default function Landing() {
  const navigate = useNavigate();
  const handleSimular = () => navigate("/login");
  const handleSolicitar = () => navigate("/register");

  const features = [
    {
      title: "Simulación Inteligente",
      text: "Conoce tu cuota y plazos en segundos con una simulación clara y transparente.",
      icon: <QueryStatsIcon fontSize="large" color="primary" />,
    },
    {
      title: "Solicitud 100% Digital",
      text: "Completa tu solicitud desde cualquier dispositivo, sin visitas a sucursal.",
      icon: <SmartphoneIcon fontSize="large" color="primary" />,
    },
    {
      title: "Evaluación Express",
      text: "Procesos ágiles para darte una respuesta rápida y oportuna.",
      icon: <BoltIcon fontSize="large" color="primary" />,
    },
    {
      title: "Firma Digital Avanzada",
      text: "Documentación firmada digitalmente, con validez legal y máxima seguridad.",
      icon: <FactCheckIcon fontSize="large" color="primary" />,
    },
    {
      title: "Desembolso Inmediato",
      text: "Una vez aprobado, recibe tu dinero en tu cuenta en minutos.",
      icon: <PaidIcon fontSize="large" color="primary" />,
    },
    {
      title: "Seguridad y Transparencia",
      text: "Tu información protegida, tu decisión informada. Todos tus datos son tratados con altos estándares de seguridad y el proceso te muestra cada paso con claridad.",
      icon: <SecurityIcon fontSize="large" color="primary" />,
    },
  ];

  return (
    <Box>
      {/* HERO */}
      <Box
        component="section"
        sx={{
          position: "relative",
          minHeight: { xs: "65vh", md: "75vh" },
          display: "flex",
          alignItems: "center",
          backgroundImage:
            "url(https://images.unsplash.com/photo-1522202176988-66273c2fd55f?q=80&w=1600&auto=format&fit=crop)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background: (t) =>
              t.palette.mode === "dark"
                ? `linear-gradient(180deg, rgba(0,0,0,.55), rgba(0,0,0,.55))`
                : `linear-gradient(180deg, rgba(255,255,255,.1), rgba(0,0,0,.35))`,
          }}
        />
        <Container maxWidth="lg" sx={{ position: "relative" }}>
          <Box sx={{ maxWidth: { xs: "100%", md: 760 } }}>
            <Typography
              variant="overline"
              sx={{ letterSpacing: 1.2, fontWeight: 700, color: "#fff", opacity: 0.9 }}
            >
              POGWARE
            </Typography>
            <Typography
              component="h1"
              variant="h3"
              fontWeight={800}
              gutterBottom
              sx={{ color: "#fff" }}
            >
              Tu Préstamo Digital, Rápido y Sin Complicaciones
            </Typography>
            <Typography variant="h6" sx={{ color: "#e6e6e6", mb: 3 }}>
              Simula, solicita y recibe tu dinero en minutos. Todo desde tu celular.
            </Typography>
            <Stack direction={{ xs: "column", sm: "row" }} spacing={2}>
              <Button variant="contained" size="large" onClick={handleSolicitar}>
                Solicita tu Crédito
              </Button>
              <Button variant="outlined" size="large" onClick={handleSimular}>
                Simula tu Crédito
              </Button>
            </Stack>
          </Box>
        </Container>
      </Box>

      {/* Trust bar */}
      <Box sx={{ py: 3 }}>
        <Container maxWidth="lg">
          <Stack
            direction="row"
            spacing={1}
            useFlexGap
            flexWrap="wrap"
            justifyContent="center"
          >
            <Chip icon={<VerifiedIcon />} label="Entidad confiable" variant="outlined" />
            <Chip icon={<LockIcon />} label="Seguridad de datos" variant="outlined" />
            <Chip icon={<SpeedIcon />} label="Respuesta rápida" variant="outlined" />
            <Chip icon={<PhoneIphoneIcon />} label="100% móvil" variant="outlined" />
          </Stack>
        </Container>
      </Box>

      {/* FEATURES */}
      <Box
        component="section"
        sx={{
          py: { xs: 6, md: 10 },
          background: (t) =>
            t.palette.mode === "dark" ? alpha(t.palette.background.paper, 0.7) : "#fafbff",
        }}
      >
        <Container maxWidth="lg">
          <Typography
            variant="h4"
            fontWeight={800}
            align="center"
            gutterBottom
            sx={{ mb: 6 }}
          >
            ¿Por qué elegirnos?
          </Typography>
          <Grid container spacing={4}>
            {features.map((f, i) => (
              <Grid key={i} item xs={12} sm={6} md={4}>
                <Card
                  elevation={0}
                  sx={{
                    p: 3,
                    height: "100%",
                    borderRadius: 3,
                    border: (t) => `1px solid ${alpha(t.palette.divider, 0.3)}`,
                    background: (t) =>
                      t.palette.mode === "dark"
                        ? alpha(t.palette.background.default, 0.8)
                        : "#fff",
                    transition: "all .3s ease",
                    "&:hover": {
                      transform: "translateY(-4px)",
                      boxShadow: (t) => `0 6px 20px ${alpha(t.palette.primary.main, 0.15)}`,
                    },
                  }}
                >
                  <Stack spacing={2} alignItems="center" textAlign="center">
                    {f.icon}
                    <Typography variant="h6" fontWeight={700}>
                      {f.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {f.text}
                    </Typography>
                  </Stack>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* CTA final */}
      <Box
        component="section"
        sx={{
          py: { xs: 6, md: 8 },
          background: (t) =>
            t.palette.mode === "dark" ? t.palette.background.paper : "#f6f9ff",
        }}
      >
        <Container maxWidth="md">
          <Paper
            elevation={0}
            sx={{
              p: { xs: 4, md: 6 },
              textAlign: "center",
              borderRadius: 3,
              border: (t) => `1px solid ${t.palette.divider}`,
            }}
          >
            <Typography variant="h5" fontWeight={800} gutterBottom>
              Listo para comenzar
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 3 }}>
              Da el siguiente paso y accede a tu Préstamo de Consumo 100% Digital.
            </Typography>
            <Stack
              direction={{ xs: "column", sm: "row" }}
              spacing={2}
              justifyContent="center"
            >
              <Button size="large" variant="contained" onClick={handleSolicitar}>
                Solicita tu Crédito
              </Button>
              <Button size="large" variant="outlined" onClick={handleSimular}>
                Simula tu Crédito
              </Button>
            </Stack>
          </Paper>
        </Container>
      </Box>

