import React, { useState } from "react";

const CONFIG = {
  TASA_BASE_ANUAL: 0.22, // 22% anual t√≠pica en Chile
  GASTOS_OPERACIONALES_PORCENTAJE: 0.02,
  COMISION_APERTURA: 0.005,
  MONTO_MIN: 100000,
  MONTO_MAX: 20000000,
  PLAZO_MIN: 6,
  PLAZO_MAX: 60,
};

export default function SimuladorCredito() {
  const [monto, setMonto] = useState(5000000);
  const [plazo, setPlazo] = useState(24);
  const [resultado, setResultado] = useState(null);
  const [historial, setHistorial] = useState([]);

  const simular = () => {
    const i = CONFIG.TASA_BASE_ANUAL / 12;
    const cuota = Math.round((monto * i) / (1 - Math.pow(1 + i, -plazo)) / 10) * 10;

    const totalPagar = cuota * plazo;
    const intereses = totalPagar - monto;
    const gastosOp = Math.round(monto * CONFIG.GASTOS_OPERACIONALES_PORCENTAJE);
    const comisionAp = Math.round(monto * CONFIG.COMISION_APERTURA);
    const liquido = monto - gastosOp - comisionAp;

    const cae = parseFloat((CONFIG.TASA_BASE_ANUAL * 1.05 * 100).toFixed(2));

    const sim = {
      id: historial.length + 1,
      cuota,
      totalPagar,
      monto,
      liquido,
      intereses,
      gastosOp,
      comisionAp,
      tasa: CONFIG.TASA_BASE_ANUAL * 100,
      cae,
      plazo,
    };

    setResultado(sim);
    setHistorial([sim, ...historial]);
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>Simulador de Cr√©dito de Consumo üí∞</h1>
        <p style={styles.subtitle}>
          Ingresa el monto y plazo que deseas para estimar tu cr√©dito.
        </p>
      </header>

      {/* Formulario */}
      <div style={styles.mainGrid}>
        <div style={styles.card}>
          <h2 style={styles.cardTitle}>Par√°metros del Cr√©dito</h2>

          <label>Monto Solicitado</label>
          <input
            type="number"
            style={styles.input}
            value={monto}
            min={CONFIG.MONTO_MIN}
            max={CONFIG.MONTO_MAX}
            onChange={(e) => setMonto(Number(e.target.value))}
          />

          <label>Plazo (meses)</label>
          <input
            type="number"
            style={styles.input}
            value={plazo}
            min={CONFIG.PLAZO_MIN}
            max={CONFIG.PLAZO_MAX}
            onChange={(e) => setPlazo(Number(e.target.value))}
          />

          <button onClick={simular} style={styles.buttonPrimary}>
            Simular Cr√©dito
          </button>
        </div>

        {/* Resultado */}
        {resultado && (
          <div style={styles.card}>
            <h2 style={styles.cardTitle}>Resultado de tu Simulaci√≥n</h2>

            <div style={styles.cuotaBox}>
              <p>Tu cuota mensual ser√°:</p>
              <p style={styles.cuotaValue}>
                ${resultado.cuota.toLocaleString("es-CL")}
              </p>
              <p>por {resultado.plazo} meses</p>
            </div>

            <div style={{ marginTop: 20 }}>
              <div style={styles.detailItem}>
                <span>üíµ Monto Solicitado:</span>
                <strong>${resultado.monto.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üí∏ Recibir√°s (l√≠quido):</span>
                <strong>${resultado.liquido.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üìà Intereses Totales:</span>
                <strong>${resultado.intereses.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üîß Gastos Operacionales:</span>
                <strong>${resultado.gastosOp.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üìù Comisi√≥n Apertura:</span>
                <strong>${resultado.comisionAp.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üí∞ Total a Pagar:</span>
                <strong>${resultado.totalPagar.toLocaleString("es-CL")}</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üìä Tasa Base:</span>
                <strong>{resultado.tasa}%</strong>
              </div>
              <div style={styles.detailItem}>
                <span>üìà CAE:</span>
                <strong>{resultado.cae}%</strong>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Historial */}
      {historial.length > 0 && (
        <section>
          <h2 style={styles.cardTitle}>Simulaciones Anteriores</h2>
          <div style={styles.historialGrid}>
            {historial.map((h) => (
              <div key={h.id} style={styles.historialCard}>
                <p>
                  <strong>${h.monto.toLocaleString("es-CL")}</strong> por{" "}
                  <strong>{h.plazo}</strong> meses
                </p>
                <p style={{ color: "#2563eb" }}>
                  Cuota: ${h.cuota.toLocaleString("es-CL")}
                </p>
                <button
                  style={styles.buttonSecondary}
                  onClick={() => setResultado(h)}
                >
                  Ver detalle
                </button>
              </div>
            ))}
          </div>
        </section>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "1200px",
    margin: "0 auto",
    padding: "32px 20px",
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: "#f9fafb",
  },
  header: {
    textAlign: "center",
    marginBottom: "40px",
  },
  title: {
    fontSize: "2.8rem",
    fontWeight: 700,
    color: "#1e293b",
    marginBottom: "12px",
    background: "linear-gradient(135deg, #2563eb, #7c3aed)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
  },
  subtitle: {
    fontSize: "1.1rem",
    color: "#64748b",
  },
  mainGrid: {
