/**
 * Módulo de Formateo
 * 
 * Funciones puras para formatear valores para presentación.
 * Maneja monedas, porcentajes, fechas, etc.
 */

/**
 * Formatea un número como moneda chilena (CLP)
 * @param {number|string} value - Valor a formatear
 * @returns {string} Valor formateado como "$1.234.567"
 */
export const formatCurrency = value => {
  const n = Number(value);
  if (!Number.isFinite(n)) {
    return "—";
  }
  return new Intl.NumberFormat("es-CL", {
    style: "currency",
    currency: "CLP",
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(Math.round(n));
};

/**
 * Formatea un número decimal como porcentaje
 * @param {number|string} value - Valor decimal (ej: 0.18 para 18%)
 * @param {number} decimals - Número de decimales (default: 2)
 * @returns {string} Valor formateado como "18.00%"
 */
export const formatPercent = (value, decimals = 2) => {
  const n = Number(value);
  if (!Number.isFinite(n)) {
    return "—";
  }
  return `${(n * 100).toFixed(decimals)}%`;
};

/**
 * Formatea una fecha/hora para display en español
 * @param {string|Date} dateString - Fecha en formato ISO o Date object
 * @param {object} options - Opciones de formato (opcional)
 * @returns {string} Fecha formateada
 */
export const formatDate = (dateString, options = {}) => {
  if (!dateString) return "—";
  const defaultOptions = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    ...options
  };
  try {
    return new Date(dateString).toLocaleDateString("es-CL", defaultOptions);
  } catch (error) {
    console.error("Error al formatear fecha:", error);
    return "—";
  }
};

/**
 * Formatea una fecha sin hora
 * @param {string|Date} dateString - Fecha en formato ISO o Date object
 * @returns {string} Fecha formateada sin hora
 */
export const formatDateShort = dateString => {
  return formatDate(dateString, {
    year: "numeric",
    month: "short",
    day: "numeric"
  });
};

/**
 * Formatea un número con separadores de miles
 * @param {number|string} value - Número a formatear
 * @returns {string} Número formateado con separadores
 */
export const formatNumber = value => {
  const n = Number(value);
  if (!Number.isFinite(n)) {
    return "—";
  }
  return new Intl.NumberFormat("es-CL").format(n);
};

/**
 * Trunca un texto largo y agrega "..."
 * @param {string} text - Texto a truncar
 * @param {number} maxLength - Longitud máxima
 * @returns {string} Texto truncado
 */
export const truncateText = (text, maxLength = 50) => {
  if (!text || text.length <= maxLength) {
    return text;
  }
  return text.substring(0, maxLength - 3) + "...";
};

/**
 * Capitaliza la primera letra de un string
 * @param {string} str - String a capitalizar
 * @returns {string} String capitalizado
 */
export const capitalize = str => {
  if (!str) return "";
  return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
};

/**
 * Formatea el estado de resultado (aprobado/rechazado)
 * @param {string} resultado - Estado del resultado
 * @returns {object} { text: string, emoji: string, color: string }
 */
export const formatResultado = resultado => {
  const resultadoLower = (resultado || "").toLowerCase();
  switch (resultadoLower) {
    case "aprobado":
      return {
        text: "Aprobado",
        emoji: "✅",
        color: "#28a745"
      };
    case "rechazado":
      return {
        text: "Rechazado",
        emoji: "❌",
        color: "#dc3545"
      };
    case "pendiente":
      return {
        text: "Pendiente",
        emoji: "⏳",
        color: "#ffc107"
      };
    default:
      return {
        text: capitalize(resultado),
        emoji: "ℹ️",
        color: "#6c757d"
      };
  }
};

