  return new Intl.NumberFormat("es-CL", {
    style: "currency",
    currency: "CLP",
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  }).format(Math.round(n));
};

/**
 * Formatea un número decimal como porcentaje
 * @param {number|string} value - Valor decimal (ej: 0.18 para 18%)
 * @param {number} decimals - Número de decimales (default: 2)
 * @returns {string} Valor formateado como "18.00%"
 */
export const formatPercent = (value, decimals = 2) => {
  const n = Number(value);

  if (!Number.isFinite(n)) {
    return "—";
  }

  return `${(n * 100).toFixed(decimals)}%`;
};

/**
 * Formatea una fecha/hora para display en español
 * @param {string|Date} dateString - Fecha en formato ISO o Date object
 * @param {object} options - Opciones de formato (opcional)
 * @returns {string} Fecha formateada
 */
export const formatDate = (dateString, options = {}) => {
  if (!dateString) return "—";

  const defaultOptions = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    ...options,
  };

  try {
    return new Date(dateString).toLocaleDateString("es-CL", defaultOptions);
  } catch (error) {
    console.error("Error al formatear fecha:", error);
    return "—";
  }
};

/**
 * Formatea una fecha sin hora
 * @param {string|Date} dateString - Fecha en formato ISO o Date object
 * @returns {string} Fecha formateada sin hora
 */
export const formatDateShort = (dateString) => {
  return formatDate(dateString, {
    year: "numeric",
    month: "short",
    day: "numeric",
  });
};

/**
 * Formatea un número con separadores de miles
 * @param {number|string} value - Número a formatear
 * @returns {string} Número formateado con separadores
 */
export const formatNumber = (value) => {
  const n = Number(value);

  if (!Number.isFinite(n)) {
