/**
 * Componente: HistorialSimulaciones
 * 
 * Muestra el historial de simulaciones guardadas.
 * Componente puramente presentacional.
 */

import React from 'react';
import { useNavigate } from 'react-router-dom';
import { formatCurrency, formatPercent, formatDate } from '../utils/formatters';

export const HistorialSimulaciones = ({ 
  historial, 
  loading, 
  mostrarTodo, 
  onEliminar, 
  onVerMas 
}) => {
  const navigate = useNavigate();

  const styles = {
    card: {
      backgroundColor: 'white',
      borderRadius: '12px',
      padding: '32px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      marginTop: '32px',
    },
    cardTitle: {
      fontSize: '1.5rem',
      marginBottom: '24px',
      color: '#2c3e50',
      borderBottom: '2px solid #3498db',
      paddingBottom: '12px',
    },
    loading: {
      textAlign: 'center',
      padding: '40px',
    },
    spinner: {
      border: '4px solid #f3f3f3',
      borderTop: '4px solid #3498db',
      borderRadius: '50%',
      width: '40px',
      height: '40px',
      animation: 'spin 1s linear infinite',
      margin: '0 auto 16px',
    },
    emptyState: {
      textAlign: 'center',
      padding: '60px 20px',
      color: '#6c757d',
    },
    historialGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
      gap: '20px',
    },
    historialCard: {
      border: '2px solid #e0e0e0',
      borderRadius: '12px',
      padding: '20px',
      cursor: 'pointer',
      transition: 'all 0.3s',
      backgroundColor: 'white',
    },
    historialCardHover: {
      borderColor: '#3498db',
      boxShadow: '0 4px 12px rgba(52,152,219,0.2)',
      transform: 'translateY(-2px)',
    },
    historialHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '16px',
    },
    historialBadge: {
      padding: '4px 12px',
      borderRadius: '12px',
      fontSize: '11px',
      fontWeight: 'bold',
      textTransform: 'uppercase',
    },
    badgeSuccess: {
      backgroundColor: '#d4edda',
      color: '#155724',
    },
    badgeError: {
      backgroundColor: '#f8d7da',
      color: '#721c24',
    },
    deleteButton: {
      background: 'none',
      border: 'none',
      color: '#dc3545',
      cursor: 'pointer',
      fontSize: '20px',
      padding: '4px 8px',
      borderRadius: '4px',
      transition: 'background-color 0.2s',
    },
    historialCuota: {
      textAlign: 'center',
      padding: '16px',
      backgroundColor: '#f8f9fa',
      borderRadius: '8px',
      marginBottom: '16px',
    },
    historialCuotaValue: {
      fontSize: '1.8rem',
      fontWeight: 'bold',
      color: '#2c3e50',
    },
    historialCuotaLabel: {
      fontSize: '12px',
      color: '#6c757d',
      marginTop: '4px',
    },
    historialDetails: {
      fontSize: '13px',
    },
    historialDetailRow: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '8px 0',
      borderBottom: '1px solid #e9ecef',
    },
    historialFooter: {
      marginTop: '12px',
      paddingTop: '12px',
      borderTop: '1px solid #e9ecef',
      fontSize: '11px',
      color: '#6c757d',
      textAlign: 'center',
    },
    buttonSecondary: {
      padding: '12px 24px',
      fontSize: '14px',
      backgroundColor: '#6c757d',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
  };

  if (loading) {
    return (
      <div style={styles.card}>
        <h2 style={styles.cardTitle}>üìö Historial de Simulaciones</h2>
        <div style={styles.loading}>
          <div style={styles.spinner}></div>
          <p>Cargando historial...</p>
        </div>
      </div>
    );
  }

  if (historial.length === 0) {
    return (
      <div style={styles.card}>
        <h2 style={styles.cardTitle}>üìö Historial de Simulaciones</h2>
        <div style={styles.emptyState}>
          <p style={{fontSize: '18px', marginBottom: '8px'}}>
            No tienes simulaciones previas
          </p>
          <p style={{fontSize: '14px', color: '#6c757d'}}>
            Guarda tu primera simulaci√≥n usando el bot√≥n "Guardar Simulaci√≥n"
          </p>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.card}>
      <h2 style={styles.cardTitle}>üìö Historial de Simulaciones</h2>

      <div style={styles.historialGrid}>
        {historial.map((sim) => (
          <div
            key={sim.id}
            style={styles.historialCard}
            onClick={() => navigate(`/simulacion/${sim.id}`)}
            onMouseEnter={(e) => {
              e.currentTarget.style.borderColor = '#3498db';
              e.currentTarget.style.boxShadow = '0 4px 12px rgba(52,152,219,0.2)';
              e.currentTarget.style.transform = 'translateY(-2px)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.borderColor = '#e0e0e0';
              e.currentTarget.style.boxShadow = 'none';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            <div style={styles.historialHeader}>
              <span style={{
                ...styles.historialBadge,
                ...(sim.resultado === 'aprobado' ? styles.badgeSuccess : styles.badgeError)
              }}>
                {sim.resultado.toUpperCase()}
              </span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onEliminar(sim.id);
                }}
                style={styles.deleteButton}
                title="Eliminar"
                onMouseEnter={(e) => e.target.style.backgroundColor = '#f8d7da'}
                onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              >
                üóëÔ∏è
              </button>
            </div>

            <div style={styles.historialCuota}>
              <div style={styles.historialCuotaValue}>
                {formatCurrency(sim.cuotaMensual)}
              </div>
              <div style={styles.historialCuotaLabel}>Cuota mensual</div>
            </div>

            <div style={styles.historialDetails}>
              <div style={styles.historialDetailRow}>
                <span>Monto:</span>
                <strong>{formatCurrency(sim.monto)}</strong>
              </div>
              <div style={styles.historialDetailRow}>
                <span>Plazo:</span>
                <strong>{sim.plazo} meses</strong>
              </div>
              <div style={styles.historialDetailRow}>
                <span>CAE:</span>
                <strong>{formatPercent(sim.cae)}</strong>
              </div>
            </div>

            <div style={styles.historialFooter}>
              {formatDate(sim.fecha)}
            </div>
          </div>
        ))}
      </div>

      {historial.length >= 4 && (
        <div style={{textAlign: 'center', marginTop: '24px'}}>
          <button
            onClick={onVerMas}
            style={styles.buttonSecondary}
            onMouseOver={(e) => e.target.style.backgroundColor = '#5a6268'}
            onMouseOut={(e) => e.target.style.backgroundColor = '#6c757d'}
          >
            {mostrarTodo ? 'Ver Menos ‚ñ≤' : 'Ver M√°s ‚ñº'}
          </button>
        </div>
      )}

      {/* CSS para animaci√≥n del spinner */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};
