import React, { useContext, useEffect, useMemo, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { 
	obtenerConfiguracion,
	crearSimulacion,
	obtenerHistorial,
	eliminarSimulacion
} from "../api/simulacion";

export default function Simulacion() {
	const { user } = useContext(AuthContext);
	const navigate = useNavigate();

	// Config remota
	const [config, setConfig] = useState(null);
	const [loadingConfig, setLoadingConfig] = useState(true);
	const [error, setError] = useState(null);
	const [success, setSuccess] = useState(null);

	// Formulario
	const [monto, setMonto] = useState(5000000);
	const [plazo, setPlazo] = useState(24);
	const [loading, setLoading] = useState(false);

	// Vista previa y tabla
	const [vistaPrevia, setVistaPrevia] = useState(null);
	const [mostrarAmortizacion, setMostrarAmortizacion] = useState(false);

	// Historial
	const [historial, setHistorial] = useState([]);
	const [loadingHistorial, setLoadingHistorial] = useState(false);
	const [mostrarTodoHistorial, setMostrarTodoHistorial] = useState(false);

	// Cargar configuraci√≥n al montar
	useEffect(() => {
		const fetchConfig = async () => {
			try {
				const res = await obtenerConfiguracion();
				setConfig(res.data);
			} catch (e) {
				console.error("Error cargando configuraci√≥n:", e);
				// Fallback sensato
				setConfig({
					tasaBaseAnual: 0.18,
					gastosOperacionalesPorcentaje: 0.02,
					comisionApertura: 0.015,
					montoMinimo: 500000,
					montoMaximo: 10000000,
					plazoMinimo: 6,
					plazoMaximo: 48,
				});
			} finally {
				setLoadingConfig(false);
			}
		};
		fetchConfig();
	}, []);

	// Utilidades de formato
	const formatCurrency = useCallback((value) => {
		const n = Number(value);
		if (!Number.isFinite(n)) return "‚Äî";
		return new Intl.NumberFormat("es-CL", {
			style: "currency",
			currency: "CLP",
			minimumFractionDigits: 0,
		}).format(Math.round(n));
	}, []);

	const formatPercent = useCallback((value) => {
		const n = Number(value);
		if (!Number.isFinite(n)) return "‚Äî";
		return `${(n * 100).toFixed(2)}%`;
	}, []);

	const formatDate = (dateString) => {
		return new Date(dateString).toLocaleDateString("es-CL", {
			year: "numeric",
			month: "long",
			day: "numeric",
			hour: "2-digit",
			minute: "2-digit",
		});
	};

	// C√°lculos financieros locales
	const calcularVistaPrevia = useCallback((montoInput, plazoInput) => {
		if (!config) return;
		const m = Number(montoInput);
		const p = Number(plazoInput);
		if (!m || !p || m <= 0 || p <= 0) {
			setVistaPrevia(null);
			return;
		}

		const gastosOp = Math.round(m * config.gastosOperacionalesPorcentaje);
		const comisionAp = Math.round(m * config.comisionApertura);
		const montoLiq = Math.round(m - gastosOp - comisionAp);

		const i = config.tasaBaseAnual / 12;
		const cuota = Math.round(m * i / (1 - Math.pow(1 + i, -p)));

		const montoTot = Math.round(cuota * p);
		const intereses = Math.round(montoTot - m);
		const cae = parseFloat((config.tasaBaseAnual + ((gastosOp + comisionAp) / m)).toFixed(6));
		const resultado = m >= config.montoMinimo && m <= config.montoMaximo && p >= config.plazoMinimo && p <= config.plazoMaximo ? 'aprobado' : 'rechazado';

		setVistaPrevia({
			monto: m,
			plazo: p,
			tasaBase: config.tasaBaseAnual,
			cae,
			cuotaMensual: cuota,
			montoTotal: montoTot,
			montoLiquido: montoLiq,
			interesesTotales: intereses,
			gastosOperacionales: gastosOp,
			comisionApertura: comisionAp,
			resultado,
		});
	}, [config]);

	useEffect(() => {
		if (!config) return;
		calcularVistaPrevia(monto, plazo);
	}, [config, monto, plazo, calcularVistaPrevia]);

	// Tabla amortizaci√≥n
	const generarTablaAmortizacion = useCallback((montoInicial, tasaMensual, meses, cuota) => {
		const tabla = [];
		let saldo = montoInicial;
		for (let k = 1; k <= meses; k++) {
			const interes = Math.round(saldo * tasaMensual);
			let amortizacion = Math.round(cuota - interes);
			if (k === meses) {
				amortizacion = saldo;
			}
			saldo = Math.max(0, saldo - amortizacion);
			tabla.push({
				periodo: k,
				cuota: Math.round(cuota),
				interes,
				amortizacion,
				saldo: Math.round(saldo),
			});
		}
		return tabla;
	}, []);

	const tablaAmortizacion = useMemo(() => {
		if (!vistaPrevia) return [];
		const i = (config?.tasaBaseAnual || 0) / 12;
		return generarTablaAmortizacion(vistaPrevia.monto, i, vistaPrevia.plazo, vistaPrevia.cuotaMensual);
	}, [vistaPrevia, config, generarTablaAmortizacion]);

	// Historial
	const cargarHistorial = useCallback(async (limit = 4) => {
		if (!user?.token) return;
		setLoadingHistorial(true);
		try {
			const res = await obtenerHistorial(user.token, limit ?? null);
			setHistorial(res.data?.simulaciones || res.data || []);
		} catch (e) {
			console.error("Error cargando historial:", e);
		} finally {
			setLoadingHistorial(false);
		}
	}, [user?.token]);

	useEffect(() => {
		cargarHistorial(4);
	}, [cargarHistorial]);

	const handleVerMas = async () => {
		const next = !mostrarTodoHistorial;
		setMostrarTodoHistorial(next);
		await cargarHistorial(next ? null : 4);
	};

	const handleEliminar = async (id) => {
		if (!user?.token) return;
		if (!confirm("¬øEliminar esta simulaci√≥n?")) return;
		try {
			await eliminarSimulacion(id, user.token);
			setSuccess("üóëÔ∏è Simulaci√≥n eliminada");
			setTimeout(() => setSuccess(null), 2500);
			await cargarHistorial(mostrarTodoHistorial ? null : 4);
		} catch (e) {
			console.error("Error eliminando:", e);
			setError("No se pudo eliminar la simulaci√≥n");
			setTimeout(() => setError(null), 3500);
		}
	};

	const handleSeleccionarSimulacion = (sim) => {
		navigate(`/simulacion/${sim.id}`);
	};

	// Submit
	const handleSubmit = async (e) => {
		e.preventDefault();
		setError(null);
		setSuccess(null);
		if (!config) {
			setError("Configuraci√≥n no disponible");
			return;
		}
		if (Number(monto) < config.montoMinimo || Number(monto) > config.montoMaximo) {
			setError(`El monto debe estar entre ${formatCurrency(config.montoMinimo)} y ${formatCurrency(config.montoMaximo)}`);
			return;
		}
		if (Number(plazo) < config.plazoMinimo || Number(plazo) > config.plazoMaximo) {
			setError(`El plazo debe estar entre ${config.plazoMinimo} y ${config.plazoMaximo} meses`);
			return;
		}
		setLoading(true);
		try {
			const res = await crearSimulacion({ monto: Number(monto), plazo: Number(plazo) }, user?.token);
			setSuccess("‚úÖ Simulaci√≥n guardada exitosamente");
			setTimeout(() => setSuccess(null), 4000);
			await cargarHistorial(mostrarTodoHistorial ? null : 4);
		} catch (e) {
			console.error("Error al simular:", e);
			setError(e.response?.data?.error || "Error al procesar la simulaci√≥n");
		} finally {
			setLoading(false);
		}
	};

	return (
		<div style={styles.container}>
			<h1 style={styles.title}>Simulador de Cr√©dito</h1>

			{error && <div style={styles.alertError}>‚ùå {error}</div>}
			{success && <div style={styles.alertSuccess}>{success}</div>}

			{loadingConfig ? (
				<div style={styles.loading}><div style={styles.spinner}></div><p>Cargando configuraci√≥n...</p></div>
			) : (
				<>
					<div style={styles.mainGrid}>
						{/* Formulario */}
						<div style={styles.card}>
							<h2 style={styles.cardTitle}>üìù Configuraci√≥n del Cr√©dito</h2>
							<div style={styles.infoBox}>
								<strong>‚ÑπÔ∏è Requisitos:</strong>
								<ul style={styles.infoList}>
									<li>Monto: {formatCurrency(config.montoMinimo)} - {formatCurrency(config.montoMaximo)}</li>
									<li>Plazo: {config.plazoMinimo} a {config.plazoMaximo} meses</li>
									<li>Tasa base anual: {formatPercent(config.tasaBaseAnual)}</li>
								</ul>
							</div>

							<form onSubmit={handleSubmit}>
								<div style={styles.formGroup}>
									<label style={styles.label}>üíµ Monto del Cr√©dito</label>
									<input type="number" value={monto} onChange={(e)=>setMonto(Number(e.target.value)||0)} style={styles.input}
										min={config.montoMinimo} max={config.montoMaximo} step="100000" />
									<input type="range" value={monto} onChange={(e)=>setMonto(Number(e.target.value)||0)} style={styles.slider}
										min={config.montoMinimo} max={config.montoMaximo} step="100000" />
									<div style={styles.rangeLabels}>
										<span>{formatCurrency(config.montoMinimo)}</span>
										<span style={styles.rangeValue}>{formatCurrency(monto)}</span>
										<span>{formatCurrency(config.montoMaximo)}</span>
									</div>
								</div>

								<div style={styles.formGroup}>
									<label style={styles.label}>üìÖ Plazo (meses)</label>
									<input type="number" value={plazo} onChange={(e)=>setPlazo(Number(e.target.value)||0)} style={styles.input}
										min={config.plazoMinimo} max={config.plazoMaximo} />
									<input type="range" value={plazo} onChange={(e)=>setPlazo(Number(e.target.value)||0)} style={styles.slider}
										min={config.plazoMinimo} max={config.plazoMaximo} />
									<div style={styles.rangeLabels}>
										<span>{config.plazoMinimo} meses</span>
										<span style={styles.rangeValue}>{plazo} meses</span>
										<span>{config.plazoMaximo} meses</span>
									</div>
								</div>

								<div style={styles.buttonGroup}>
									<button type="submit" disabled={loading} style={{...styles.button, ...styles.buttonPrimary, ...(loading?styles.buttonDisabled:{})}}>
										{loading ? "üíæ Guardando..." : "üíæ Guardar Simulaci√≥n"}
									</button>
									<button type="button" onClick={()=>{ setMonto(config.montoMinimo); setPlazo(config.plazoMinimo); }}
										style={{...styles.button, ...styles.buttonSecondary}}>Reiniciar</button>
								</div>
							</form>
						</div>

						{/* Vista previa */}
						<div style={styles.card}>
							<h2 style={styles.cardTitle}>üìä Vista Previa</h2>
							{vistaPrevia ? (
								<>
									<div style={{...styles.badge, ...(vistaPrevia.resultado==='aprobado'?styles.badgeSuccess:styles.badgeError)}}>
										{vistaPrevia.resultado === 'aprobado' ? '‚úÖ Aprobado' : '‚ùå Rechazado'}
									</div>
									<div style={styles.cuotaBox}>
										<div style={styles.cuotaLabel}>Tu cuota mensual ser√°:</div>
										<div style={styles.cuotaValue}>{formatCurrency(vistaPrevia.cuotaMensual)}</div>
										<div style={styles.cuotaSubtext}>por {vistaPrevia.plazo} meses</div>
									</div>

									<div style={styles.detailsGrid}>
										<div style={styles.detailItem}><span>üí∞ Total a Pagar:</span><strong>{formatCurrency(vistaPrevia.montoTotal)}</strong></div>
										<div style={styles.detailItem}><span>üíµ Monto Solicitado:</span><strong>{formatCurrency(vistaPrevia.monto)}</strong></div>
										<div style={styles.detailItem}><span>üí∏ Recibir√°s (l√≠quido):</span><strong style={{color:'#28a745'}}>{formatCurrency(vistaPrevia.montoLiquido)}</strong></div>
										<div style={styles.detailItem}><span>üìà Intereses Totales:</span><strong style={{color:'#dc3545'}}>{formatCurrency(vistaPrevia.interesesTotales)}</strong></div>
										<div style={styles.detailItem}><span>üîß Gastos Operacionales:</span><strong>{formatCurrency(vistaPrevia.gastosOperacionales)}</strong></div>
										<div style={styles.detailItem}><span>üìù Comisi√≥n Apertura:</span><strong>{formatCurrency(vistaPrevia.comisionApertura)}</strong></div>
										<div style={styles.detailItem}><span>üìä Tasa Base:</span><strong>{formatPercent(vistaPrevia.tasaBase)}</strong></div>
										<div style={styles.detailItem}><span>üìà CAE:</span><strong style={{color:'#ff6b6b'}}>{formatPercent(vistaPrevia.cae)}</strong></div>
									</div>

									<div style={styles.warningBox}>
										<strong>üí° ¬øQu√© es el CAE?</strong>
										<p style={{margin:'8px 0 0 0', fontSize:'13px'}}>La Carga Anual Equivalente incluye todos los costos del cr√©dito (tasa base + gastos + comisiones) y te permite comparar opciones.</p>
									</div>

									<div style={{marginTop:'16px'}}>
										<button style={{...styles.button, ...styles.buttonSecondary}}
											onClick={()=>setMostrarAmortizacion(v=>!v)}>
											{mostrarAmortizacion ? 'Ocultar Tabla' : 'Ver Tabla de Amortizaci√≥n'}
										</button>
									</div>

									{mostrarAmortizacion && (
										<div style={{marginTop:'16px', overflowX:'auto'}}>
											<table style={styles.table}>
												<thead>
													<tr>
														<th>Periodo</th><th>Cuota</th><th>Inter√©s</th><th>Amortizaci√≥n</th><th>Saldo</th>
													</tr>
												</thead>
												<tbody>
													{tablaAmortizacion.map((fila)=> (
														<tr key={fila.periodo}>
															<td>{fila.periodo}</td>
															<td>{formatCurrency(fila.cuota)}</td>
															<td>{formatCurrency(fila.interes)}</td>
															<td>{formatCurrency(fila.amortizacion)}</td>
															<td>{formatCurrency(fila.saldo)}</td>
														</tr>
													))}
												</tbody>
											</table>
										</div>
									)}
								</>
							) : (
								<div style={styles.emptyState}><p>üìã Ajusta los valores para ver la simulaci√≥n</p></div>
							)}
						</div>
					</div>

					{/* Historial */}
					<div style={styles.card}>
						<h2 style={styles.cardTitle}>üìö Historial de Simulaciones</h2>
						{loadingHistorial ? (
							<div style={styles.loading}><div style={styles.spinner}></div><p>Cargando historial...</p></div>
						) : historial.length === 0 ? (
							<div style={styles.emptyState}>
								<p style={{fontSize:'18px', marginBottom:'8px'}}>No tienes simulaciones previas</p>
								<p style={{fontSize:'14px', color:'#6c757d'}}>Guarda tu primera simulaci√≥n usando el bot√≥n "Guardar Simulaci√≥n"</p>
							</div>
						) : (
							<>
								<div style={styles.historialGrid}>
									{historial.map((sim)=> (
										<div key={sim.id} style={styles.historialCard} onClick={()=>handleSeleccionarSimulacion(sim)}>
											<div style={styles.historialHeader}>
												<span style={{...styles.historialBadge, ...(sim.resultado==='aprobado'?styles.badgeSuccess:styles.badgeError)}}>
													{sim.resultado?.toUpperCase?.() || sim.resultado}
												</span>
												<button
													onClick={(e)=>{ e.stopPropagation(); handleEliminar(sim.id); }}
													style={styles.deleteButton}
													title="Eliminar"
												>üóëÔ∏è</button>
											</div>
											<div style={styles.historialCuota}>
												<div style={styles.historialCuotaValue}>{formatCurrency(sim.cuotaMensual)}</div>
												<div style={styles.historialCuotaLabel}>Cuota mensual</div>
											</div>
											<div style={styles.historialDetails}>
												<div style={styles.historialDetailRow}><span>Monto:</span><strong>{formatCurrency(sim.monto)}</strong></div>
												<div style={styles.historialDetailRow}><span>Plazo:</span><strong>{sim.plazo} meses</strong></div>
												<div style={styles.historialDetailRow}><span>CAE:</span><strong>{formatPercent(sim.cae)}</strong></div>
											</div>
											<div style={styles.historialFooter}>{formatDate(sim.fecha)}</div>
										</div>
									))}
								</div>
								{historial.length >= 4 && (
									<div style={{textAlign:'center', marginTop:'24px'}}>
										<button onClick={handleVerMas} style={{...styles.button, ...styles.buttonSecondary}}>
											{mostrarTodoHistorial ? 'Ver Menos ‚ñ≤' : 'Ver M√°s ‚ñº'}
										</button>
									</div>
								)}
							</>
						)}
					</div>
				</>
			)}
		</div>
	);
}

// Estilos inline
const styles = {
	container: {
		maxWidth: '1400px',
		margin: '0 auto',
		padding: '24px'
	},
	title: {
		textAlign: 'center',
		marginBottom: '16px',
		fontSize: '2rem'
	},
	alertError: { backgroundColor: '#f8d7da', color: '#721c24', padding: '12px', borderRadius: '6px', marginBottom: '12px' },
	alertSuccess: { backgroundColor: '#d4edda', color: '#155724', padding: '12px', borderRadius: '6px', marginBottom: '12px' },
	loading: { display:'flex', alignItems:'center', gap:'12px' },
	spinner: { width:'18px', height:'18px', border:'2px solid #ccc', borderTop:'2px solid #333', borderRadius:'50%', animation:'spin 1s linear infinite' },
	mainGrid: {
		display: 'grid',
		gridTemplateColumns: '1fr 1fr',
		gap: '16px',
		alignItems: 'start',
	},
	card: { backgroundColor: 'white', border: '1px solid #eaeaea', borderRadius: '8px', padding: '16px' },
	cardTitle: { marginTop: 0, marginBottom: '12px' },
	infoBox: { backgroundColor: '#f7f7f9', border: '1px dashed #ccc', padding: '12px', borderRadius: '6px', marginBottom: '16px' },
	infoList: { margin: 0, paddingLeft: '18px' },
	formGroup: { marginBottom: '16px' },
	label: { display: 'block', marginBottom: '6px', fontWeight: 600 },
	input: { width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '6px' },
	slider: { width: '100%', marginTop: '10px' },
	rangeLabels: { display:'flex', justifyContent:'space-between', marginTop:'8px', fontSize:'12px', color:'#6c757d' },
	rangeValue: { fontWeight: 600, color: '#333' },
	buttonGroup: { display:'flex', gap:'10px', marginTop:'8px' },
	button: { padding: '10px 14px', borderRadius: '6px', border: '1px solid #ccc', cursor: 'pointer', background: '#fff' },
	buttonPrimary: { backgroundColor: '#007bff', color:'#fff', border: '1px solid #007bff' },
	buttonSecondary: { backgroundColor: '#f1f3f5' },
	buttonDisabled: { opacity: 0.7, cursor: 'not-allowed' },
	badge: { display:'inline-block', padding:'4px 8px', borderRadius:'999px', fontSize:'12px', marginBottom:'10px' },
	badgeSuccess: { backgroundColor:'#d4edda', color:'#155724' },
	badgeError: { backgroundColor:'#f8d7da', color:'#721c24' },
	cuotaBox: { textAlign:'center', padding:'12px', background:'#f8f9fa', borderRadius:'8px', marginBottom:'12px' },
	cuotaLabel: { fontSize:'14px', color:'#6c757d' },
	cuotaValue: { fontSize:'28px', fontWeight:'bold' },
	cuotaSubtext: { fontSize:'12px', color:'#6c757d' },
	detailsGrid: { display:'grid', gridTemplateColumns:'1fr 1fr', gap:'10px' },
	detailItem: { display:'flex', justifyContent:'space-between', padding:'8px', background:'#f8f9fa', borderRadius:'6px' },
	warningBox: { background:'#fff3cd', color:'#856404', border:'1px solid #ffeeba', padding:'10px', borderRadius:'6px', marginTop:'12px' },
	emptyState: { textAlign:'center', color:'#6c757d' },
	table: { width:'100%', borderCollapse:'collapse' },
	historialGrid: { display:'grid', gap:'12px', gridTemplateColumns:'repeat(auto-fill, minmax(280px,1fr))' },
	historialCard: { border:'1px solid #eaeaea', borderRadius:'8px', padding:'12px', cursor:'pointer', backgroundColor:'#fff' },
	historialHeader: { display:'flex', justifyContent:'space-between', alignItems:'center' },
	historialBadge: { padding:'4px 8px', borderRadius:'999px', fontSize:'12px' },
	deleteButton: { background:'transparent', border:'none', cursor:'pointer', fontSize:'16px' },
	historialCuota: { textAlign:'center', marginTop:'6px' },
	historialCuotaValue: { fontSize:'20px', fontWeight:'bold' },
	historialCuotaLabel: { fontSize:'12px', color:'#6c757d' },
	historialDetails: { marginTop:'8px' },
	historialDetailRow: { display:'flex', justifyContent:'space-between' },
	historialFooter: { marginTop:'8px', fontSize:'12px', color:'#6c757d' },
};
