/**
 * Componente: SimulacionForm
 * 
 * Formulario para configurar monto y plazo del cr茅dito.
 * Componente puramente presentacional.
 */

import React from 'react';
import { formatCurrency, formatPercent } from '../utils/formatters';

export const SimulacionForm = ({ 
  monto, 
  setMonto, 
  plazo, 
  setPlazo, 
  config, 
  loading, 
  onSubmit 
}) => {
  if (!config) return null;

  const styles = {
    card: {
      backgroundColor: 'white',
      borderRadius: '12px',
      padding: '32px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    },
    cardTitle: {
      fontSize: '1.5rem',
      marginBottom: '24px',
      color: '#2c3e50',
      borderBottom: '2px solid #3498db',
      paddingBottom: '12px',
    },
    infoBox: {
      backgroundColor: '#e3f2fd',
      border: '1px solid #90caf9',
      borderRadius: '8px',
      padding: '16px',
      marginBottom: '24px',
      fontSize: '14px',
    },
    infoList: {
      margin: '8px 0 0 20px',
      paddingLeft: '0',
    },
    formGroup: {
      marginBottom: '24px',
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '600',
      color: '#34495e',
      fontSize: '15px',
    },
    input: {
      width: '100%',
      padding: '12px',
      fontSize: '16px',
      border: '2px solid #e0e0e0',
      borderRadius: '8px',
      marginBottom: '12px',
      transition: 'border-color 0.3s',
    },
    slider: {
      width: '100%',
      height: '6px',
      borderRadius: '3px',
      outline: 'none',
      marginBottom: '8px',
    },
    rangeLabels: {
      display: 'flex',
      justifyContent: 'space-between',
      fontSize: '12px',
      color: '#7f8c8d',
    },
    rangeValue: {
      fontWeight: 'bold',
      color: '#3498db',
    },
    buttonPrimary: {
      width: '100%',
      padding: '16px',
      fontSize: '16px',
      fontWeight: 'bold',
      backgroundColor: '#3498db',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
    buttonDisabled: {
      backgroundColor: '#95a5a6',
      cursor: 'not-allowed',
    },
  };

  return (
    <div style={styles.card}>
      <h2 style={styles.cardTitle}> Configuraci贸n del Cr茅dito</h2>

      {/* Info Box */}
      <div style={styles.infoBox}>
        <strong>癸 Requisitos:</strong>
        <ul style={styles.infoList}>
          <li>Monto: {formatCurrency(config.montoMinimo)} - {formatCurrency(config.montoMaximo)}</li>
          <li>Plazo: {config.plazoMinimo} a {config.plazoMaximo} meses</li>
          <li>Tasa base anual: {formatPercent(config.tasaBaseAnual)}</li>
        </ul>
      </div>

      <form onSubmit={onSubmit}>
        {/* Monto */}
        <div style={styles.formGroup}>
          <label style={styles.label}>
             Monto del Cr茅dito
          </label>
          <input
            type="number"
            value={monto}
            onChange={(e) => setMonto(Number(e.target.value) || 0)}
            style={styles.input}
            min={config.montoMinimo}
            max={config.montoMaximo}
            step="100000"
          />
          <input
            type="range"
            value={monto}
            onChange={(e) => setMonto(Number(e.target.value) || 0)}
            style={styles.slider}
            min={config.montoMinimo}
            max={config.montoMaximo}
            step="100000"
          />
          <div style={styles.rangeLabels}>
            <span>{formatCurrency(config.montoMinimo)}</span>
            <span style={styles.rangeValue}>{formatCurrency(monto)}</span>
            <span>{formatCurrency(config.montoMaximo)}</span>
          </div>
        </div>

        {/* Plazo */}
        <div style={styles.formGroup}>
          <label style={styles.label}>
             Plazo (meses)
          </label>
          <input
            type="number"
            value={plazo}
            onChange={(e) => setPlazo(Number(e.target.value) || 0)}
            style={styles.input}
            min={config.plazoMinimo}
            max={config.plazoMaximo}
          />
          <input
            type="range"
            value={plazo}
            onChange={(e) => setPlazo(Number(e.target.value) || 0)}
            style={styles.slider}
            min={config.plazoMinimo}
            max={config.plazoMaximo}
          />
          <div style={styles.rangeLabels}>
            <span>{config.plazoMinimo} meses</span>
            <span style={styles.rangeValue}>{plazo} meses</span>
            <span>{config.plazoMaximo} meses</span>
          </div>
        </div>

        {/* Bot贸n */}
        <button
          type="submit"
          disabled={loading}
          style={{
            ...styles.buttonPrimary,
            ...(loading ? styles.buttonDisabled : {}),
          }}
          onMouseOver={(e) => !loading && (e.target.style.backgroundColor = '#2980b9')}
          onMouseOut={(e) => !loading && (e.target.style.backgroundColor = '#3498db')}
        >
          {loading ? ' Guardando...' : ' Guardar Simulaci贸n'}
        </button>
      </form>
    </div>
  );
};
