    borderRadius: '12px'
  },
  deleteButton: {
    background: 'none',
    border: 'none',
    fontSize: '18px',
    cursor: 'pointer',
    padding: '4px 8px'
  },
  historialCuota: {
    marginBottom: '16px'
  },
  historialCuotaValue: {
    fontSize: '24px',
    fontWeight: 'bold',
    color: '#667eea'
  },
  historialCuotaLabel: {
    fontSize: '12px',
    color: '#6c757d'
  },
  historialDetails: {
    fontSize: '14px',
    marginBottom: '12px'
  },
  historialDetailRow: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '4px'
  },
  historialFooter: {
    fontSize: '11px',
    color: '#999',
    paddingTop: '12px',
    borderTop: '1px solid #eee'
  }
};

// Agregar animaciÃ³n para el spinner
const styleSheet = document.createElement("style");
styleSheet.textContent = `
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  .historial-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
    border-color: #667eea;
  }
`;
