// src/pages/Landing.jsx
import React from "react";
import {
  Box,
  Button,
  Card,
  Container,
  Grid,
  Paper,
  Stack,
  Typography,
} from "@mui/material";
import { alpha } from "@mui/material/styles";
import {
  QueryStats as QueryStatsIcon,
  Smartphone as SmartphoneIcon,
  Bolt as BoltIcon,
  FactCheck as FactCheckIcon,
  Paid as PaidIcon,
  Security as SecurityIcon,
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";

const features = [
  {
    title: "Simulación Inteligente",
    text: "Conoce tu cuota y plazos en segundos con una simulación clara y transparente.",
    icon: <QueryStatsIcon fontSize="medium" />,
  },
  {
    title: "Solicitud 100% Digital",
    text: "Completa tu solicitud desde cualquier dispositivo, sin visitas a sucursal.",
    icon: <SmartphoneIcon fontSize="medium" />,
  },
  {
    title: "Evaluación Express",
    text: "Procesos ágiles para darte una respuesta rápida y oportuna.",
    icon: <BoltIcon fontSize="medium" />,
  },
  {
    title: "Firma Digital Avanzada",
    text: "Documentación firmada digitalmente, con validez legal y máxima seguridad.",
    icon: <FactCheckIcon fontSize="medium" />,
  },
  {
    title: "Desembolso Inmediato",
    text: "Una vez aprobado, recibe tu dinero en tu cuenta en minutos.",
    icon: <PaidIcon fontSize="medium" />,
  },
  {
    title: "Seguridad y Transparencia",
    text: "Tu información protegida, tu decisión informada. Sin letras chicas, sin sorpresas.",
    icon: <SecurityIcon fontSize="medium" />,
  },
];

const FeatureCard = ({ icon, title, text }) => (
  <Card
    variant="outlined"
    sx={{
      borderRadius: 3,
      borderColor: (t) => t.palette.divider,
      p: 3,
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      height: "100%",
      minHeight: { xs: 220, md: 260 },
      maxHeight: { xs: 260, md: 280 },
    }}
  >
    <Box
      sx={{
        width: 48,
        height: 48,
        borderRadius: 2,
        display: "inline-flex",
        alignItems: "center",
        justifyContent: "center",
        color: "primary.main",
        bgcolor: (t) => alpha(t.palette.primary.main, 0.08),
        mb: 1,
      }}
    >
      {icon}
    </Box>
    <Typography variant="h6" fontWeight={700} sx={{ mb: 0.5 }}>
      {title}
    </Typography>
    <Typography
      variant="body2"
      color="text.secondary"
      sx={{
        flexGrow: 1,
        overflow: "hidden",
        display: "-webkit-box",
        WebkitLineClamp: 4,
        WebkitBoxOrient: "vertical",
      }}
    >
      {text}
    </Typography>
  </Card>
);

export default function Landing() {
  const navigate = useNavigate();

  const handleSimular = () => navigate("/login");
  const handleSolicitar = () => navigate("/register");

  return (
    <Box>
      {/* HERO */}
      <Box
        component="section"
        sx={{
          pt: { xs: 8, md: 12 },
          pb: { xs: 8, md: 12 },
          background: (theme) =>
            theme.palette.mode === "dark"
              ? `linear-gradient(180deg, ${theme.palette.background.default} 0%, ${theme.palette.background.paper} 100%)`
              : `linear-gradient(180deg, #f8fafc 0%, #ffffff 100%)`,
        }}
      >
        <Container maxWidth="lg">
          <Paper
            elevation={0}
            sx={{
              p: { xs: 3, md: 5 },
              borderRadius: 3,
              border: (t) => `1px solid ${t.palette.divider}`,
              backgroundColor: (t) =>
                t.palette.mode === "dark"
                  ? t.palette.background.paper
                  : "#fff",
            }}
          >
            <Grid container spacing={6} alignItems="center">
              {/* Texto principal */}
              <Grid item xs={12} md={6}>
                <Typography
                  variant="overline"
                  sx={{
                    letterSpacing: 1.2,
                    fontWeight: 700,
                    color: "text.secondary",
                  }}
                >
                  POGWARE
                </Typography>
                <Typography
                  component="h1"
                  variant="h3"
                  fontWeight={800}
                  gutterBottom
                  sx={{ mt: 1 }}
                >
                  Tu Préstamo Digital, Rápido y Sin Complicaciones
                </Typography>
                <Typography variant="h6" color="text.secondary">
                  Simula, solicita y recibe tu dinero en minutos. Todo desde tu
                  celular.
                </Typography>
              </Grid>

              {/* Imagen */}
              <Grid item xs={12} md={6}>
                <Box
                  component="img"
                  src="https://images.unsplash.com/photo-1518717758536-85ae29035b6d?q=80&w=1200&auto=format&fit=crop"
                  alt="Persona feliz usando su celular"
                  sx={{
                    width: "100%",
                    borderRadius: 3,
                    boxShadow: 6,
                    display: "block",
                    border: (t) => `1px solid ${t.palette.divider}`,
                  }}
                />
              </Grid>
            </Grid>
          </Paper>
        </Container>
      </Box>

      {/* JUMBOTRON */}
      <Box component="section" sx={{ py: { xs: 8, md: 10 } }}>
        <Container maxWidth="md">
          <Paper
            elevation={0}
            sx={{
              p: { xs: 4, md: 6 },
              textAlign: "center",
              borderRadius: 3,
              border: (t) => `1px solid ${t.palette.divider}`,
              backgroundImage: (t) =>
                t.palette.mode === "dark"
                  ? `linear-gradient(180deg, ${t.palette.background.paper} 0%, ${t.palette.background.default} 100%)`
                  : `linear-gradient(180deg, #ffffff 0%, #f9fbff 100%)`,
            }}
          >
            <Typography variant="h4" fontWeight={800} gutterBottom>
              Transformamos la forma de acceder a tu préstamo
            </Typography>
            <Typography
              variant="body1"
              color="text.secondary"
              sx={{ mb: 3 }}
            >
              Somos una institución con años de experiencia en el mercado
              financiero. Hoy damos un paso adelante con nuestro nuevo servicio
              de Préstamos de Consumo Digital, diseñado para que puedas acceder
              a tu crédito de forma rápida, segura y 100% online. Sin filas, sin
              papeleo, sin esperas.
            </Typography>

            <Stack
              direction={{ xs: "column", sm: "row" }}
              spacing={2}
              justifyContent="center"
            >
              <Button size="large" variant="contained" onClick={handleSimular}>
                Simula tu Crédito
              </Button>
              <Button size="large" variant="outlined" onClick={handleSolicitar}>
                Solicita tu Crédito
              </Button>
            </Stack>
          </Paper>
        </Container>
      </Box>

      {/* FEATURES */}
      <Box component="section" sx={{ py: { xs: 10, md: 12 } }}>
        <Container maxWidth="lg">
          <Typography
            variant="h4"
            fontWeight={800}
            textAlign="center"
            gutterBottom
          >
            ¿Por qué elegir nuestro Préstamo Digital?
          </Typography>

          <Grid container spacing={3} alignItems="stretch" sx={{ mt: 4 }}>
            {features.map((feature, index) => (
              <Grid item key={index} xs={12} sm={6} md={4} sx={{ display: "flex" }}>
                <FeatureCard {...feature} />
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* FOOTER */}
      <Box
        component="footer"
        sx={{
          py: 4,
          borderTop: (t) => `1px solid ${t.palette.divider}`,
        }}
      >
        <Container maxWidth="lg">
          <Typography
            variant="body2"
            color="text.secondary"
            align="center"
          >
            © {new Date().getFullYear()} POGWARE · Créditos Financieros
          </Typography>
        </Container>
      </Box>
    </Box>
  );
}
