/**
 * Componente: VistaPrevia
 * 
 * Muestra la vista previa de la simulaci√≥n con todos los c√°lculos.
 * Componente puramente presentacional.
 */

import React from 'react';
import { formatCurrency, formatPercent } from '../utils/formatters';

export const VistaPrevia = ({ vistaPrevia, onToggleAmortizacion, mostrarAmortizacion }) => {
  const styles = {
    card: {
      backgroundColor: 'white',
      borderRadius: '12px',
      padding: '32px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    },
    cardTitle: {
      fontSize: '1.5rem',
      marginBottom: '24px',
      color: '#2c3e50',
      borderBottom: '2px solid #3498db',
      paddingBottom: '12px',
    },
    badge: {
      display: 'inline-block',
      padding: '8px 16px',
      borderRadius: '20px',
      fontSize: '14px',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    badgeSuccess: {
      backgroundColor: '#d4edda',
      color: '#155724',
      border: '1px solid #c3e6cb',
    },
    badgeError: {
      backgroundColor: '#f8d7da',
      color: '#721c24',
      border: '1px solid #f5c6cb',
    },
    cuotaBox: {
      backgroundColor: '#f8f9fa',
      borderRadius: '12px',
      padding: '24px',
      textAlign: 'center',
      marginBottom: '24px',
      border: '2px solid #e9ecef',
    },
    cuotaLabel: {
      fontSize: '14px',
      color: '#6c757d',
      marginBottom: '8px',
    },
    cuotaValue: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      color: '#2c3e50',
      margin: '8px 0',
    },
    cuotaSubtext: {
      fontSize: '14px',
      color: '#6c757d',
    },
    detailsGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '12px',
      marginBottom: '20px',
    },
    detailItem: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '12px',
      backgroundColor: '#f8f9fa',
      borderRadius: '6px',
      fontSize: '14px',
    },
    warningBox: {
      backgroundColor: '#fff3cd',
      border: '1px solid #ffc107',
      borderRadius: '8px',
      padding: '16px',
      fontSize: '14px',
      marginBottom: '20px',
    },
    buttonSecondary: {
      width: '100%',
      padding: '12px',
      fontSize: '14px',
      backgroundColor: '#6c757d',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
    emptyState: {
      textAlign: 'center',
      padding: '60px 20px',
      color: '#6c757d',
      fontSize: '16px',
    },
  };

  if (!vistaPrevia) {
    return (
      <div style={styles.card}>
        <h2 style={styles.cardTitle}>üìä Vista Previa</h2>
        <div style={styles.emptyState}>
          <p>üìã Ajusta los valores para ver la simulaci√≥n</p>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.card}>
      <h2 style={styles.cardTitle}>üìä Vista Previa</h2>

      {/* Badge de Estado */}
      <div style={{
        ...styles.badge,
        ...(vistaPrevia.resultado === 'aprobado' ? styles.badgeSuccess : styles.badgeError)
      }}>
        {vistaPrevia.resultado === 'aprobado' ? '‚úÖ Aprobado' : '‚ùå Rechazado'}
      </div>

      {/* Cuota Destacada */}
      <div style={styles.cuotaBox}>
        <div style={styles.cuotaLabel}>Tu cuota mensual ser√°:</div>
        <div style={styles.cuotaValue}>{formatCurrency(vistaPrevia.cuotaMensual)}</div>
        <div style={styles.cuotaSubtext}>por {vistaPrevia.plazo} meses</div>
      </div>

      {/* Detalles */}
      <div style={styles.detailsGrid}>
        <div style={styles.detailItem}>
          <span>üí∞ Total a Pagar:</span>
          <strong>{formatCurrency(vistaPrevia.montoTotal)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üíµ Monto Solicitado:</span>
          <strong>{formatCurrency(vistaPrevia.monto)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üí∏ Recibir√°s (l√≠quido):</span>
          <strong style={{color: '#28a745'}}>{formatCurrency(vistaPrevia.montoLiquido)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üìà Intereses Totales:</span>
          <strong style={{color: '#dc3545'}}>{formatCurrency(vistaPrevia.interesesTotales)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üîß Gastos Operacionales:</span>
          <strong>{formatCurrency(vistaPrevia.gastosOperacionales)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üìù Comisi√≥n Apertura:</span>
          <strong>{formatCurrency(vistaPrevia.comisionApertura)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üìä Tasa Base:</span>
          <strong>{formatPercent(vistaPrevia.tasaBase)}</strong>
        </div>
        <div style={styles.detailItem}>
          <span>üìà CAE:</span>
          <strong style={{color: '#ff6b6b'}}>{formatPercent(vistaPrevia.cae)}</strong>
        </div>
      </div>

      {/* Info CAE */}
      <div style={styles.warningBox}>
        <strong>üí° ¬øQu√© es el CAE?</strong>
        <p style={{margin: '8px 0 0 0', fontSize: '13px'}}>
          La Carga Anual Equivalente incluye todos los costos del cr√©dito 
          (tasa base + gastos + comisiones) y te permite comparar diferentes opciones.
        </p>
      </div>

      {/* Bot√≥n Ver Amortizaci√≥n */}
      <button
        onClick={onToggleAmortizacion}
        style={styles.buttonSecondary}
        onMouseOver={(e) => e.target.style.backgroundColor = '#5a6268'}
        onMouseOut={(e) => e.target.style.backgroundColor = '#6c757d'}
      >
        {mostrarAmortizacion ? 'üîº Ocultar Tabla de Amortizaci√≥n' : 'üîΩ Ver Tabla de Amortizaci√≥n'}
      </button>
    </div>
  );
};
