// ./frontend/pages/Simulacion.js
import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { crearSimulacion, obtenerHistorial, eliminarSimulacion } from "../api/simulacion";
import {
  Box,
  Button,
  Card,
  CircularProgress,
  Container,
  Grid,
  Stack,
  TextField,
  Typography,
  Divider,
  Paper,
} from "@mui/material";

export default function Simulacion() {
  const { user } = useContext(AuthContext);
  const [monto, setMonto] = useState("");
  const [plazo, setPlazo] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [historial, setHistorial] = useState([]);
  const [simulacion, setSimulacion] = useState(null);

  // Cargar historial
  const cargarHistorial = async () => {
    try {
      const response = await obtenerHistorial(user.token);
      setHistorial(response.data);
    } catch (err) {
      console.error("Error al cargar historial:", err);
    }
  };

  useEffect(() => {
    cargarHistorial();
  }, []);

  const handleSimular = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    setSimulacion(null);

    if (!monto || !plazo) {
      setError("Por favor completa monto y plazo.");
      return;
    }

    setLoading(true);
    try {
      const response = await crearSimulacion({ monto, plazo }, user.token);
      setSimulacion(response.data);
      setSuccess("✅ Simulación generada correctamente");
      cargarHistorial();
    } catch (err) {
      console.error("Error al crear simulación:", err);
      setError(err.response?.data?.error || "Error al procesar la simulación");
    } finally {
      setLoading(false);
    }
  };

  const handleEliminar = async (id) => {
    if (!window.confirm("¿Eliminar esta simulación?")) return;
    try {
      await eliminarSimulacion(id, user.token);
      cargarHistorial();
    } catch (err) {
      console.error("Error al eliminar simulación:", err);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper sx={{ p: 4, borderRadius: 3, boxShadow: 3 }}>
        <Typography variant="h5" fontWeight="bold" gutterBottom>
          Simulador de Crédito
        </Typography>

        <Box component="form" onSubmit={handleSimular} sx={{ mt: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Monto solicitado"
                fullWidth
                value={monto}
                onChange={(e) => setMonto(e.target.value)}
                type="number"
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Plazo (meses)"
                fullWidth
                value={plazo}
                onChange={(e) => setPlazo(e.target.value)}
                type="number"
                required
              />
            </Grid>
          </Grid>

          <Stack direction="row" spacing={2} mt={3} alignItems="center">
            <Button type="submit" variant="contained" disabled={loading}>
              {loading ? <CircularProgress size={24} /> : "Simular"}
            </Button>
            {error && <Typography color="error">{error}</Typography>}
            {success && <Typography color="success.main">{success}</Typography>}
          </Stack>
        </Box>

        {simulacion && (
          <Card sx={{ mt: 4, p: 3 }}>
            <Typography variant="h6">Resultado de la simulación</Typography>
            <Divider sx={{ my: 2 }} />
            <Typography>Monto solicitado: ${simulacion.monto.toLocaleString()}</Typography>
            <Typography>Plazo: {simulacion.plazo} meses</Typography>
            <Typography>Tasa base anual: {(simulacion.tasa_base * 100).toFixed(2)}%</Typography>
            <Typography>Cuota mensual: ${simulacion.cuota_mensual.toLocaleString()}</Typography>
            <Typography>Monto total a pagar: ${simulacion.monto_total.toLocaleString()}</Typography>
            <Typography>CAE: {(simulacion.cae * 100).toFixed(2)}%</Typography>
            <Typography sx={{ mt: 1 }}>
              <strong>Resultado:</strong> {simulacion.resultado.toUpperCase()}
            </Typography>
          </Card>
        )}

        {historial.length > 0 && (
          <Box mt={5}>
            <Typography variant="h6" gutterBottom>
              Historial de simulaciones
            </Typography>
            {historial.map((s) => (
              <Card key={s.id} sx={{ p: 2, mb: 2 }}>
                <Grid container>
                  <Grid item xs={8}>
                    <Typography>
                      <strong>${s.monto.toLocaleString()}</strong> — {s.plazo} meses
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Cuota: ${s.cuota_mensual.toLocaleString()} | Resultado: {s.resultado}
                    </Typography>
                  </Grid>
                  <Grid item xs={4} textAlign="right">
                    <Button
                      color="error"
                      onClick={() => handleEliminar(s.id)}
                      size="small"
                      variant="outlined"
                    >
                      Eliminar
                    </Button>
                  </Grid>
                </Grid>
              </Card>
            ))}
          </Box>
        )}
      </Paper>
    </Container>
  );
}
