import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

// Simulación de API call (reemplaza por fetch real)
async function fetchHistorial(token) {
  // Aquí deberías hacer un fetch real a tu microservicio
  // return fetch('/api/simulaciones', { headers: { Authorization: `Bearer ${token}` } })
  //   .then(r => r.json());
  return [
    { id: 1, monto: 10000, plazo: 12, tasa: 0.25, cuota: 950.5, resultado: "aprobado", fecha: "2025-11-01" },
    { id: 2, monto: 500, plazo: 6, tasa: 0.3, cuota: 90.2, resultado: "rechazado", fecha: "2025-10-20" },
  ];
}

export default function Simulacion() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [historial, setHistorial] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }
    fetchHistorial(user.token).then(setHistorial);
  }, [user, navigate]);

  return (
    <div style={{ padding: 32, maxWidth: 600, margin: "0 auto" }}>
      <h2>Simulación de Crédito</h2>
      {/* Aquí iría el formulario de simulación */}
      <div style={{ margin: "32px 0" }}>
        <h3>Historial de simulaciones</h3>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Monto</th>
              <th>Plazo</th>
              <th>Tasa</th>
              <th>Cuota</th>
              <th>Resultado</th>
              <th>Fecha</th>
            </tr>
          </thead>
          <tbody>
            {historial.map((s) => (
              <tr key={s.id}>
                <td>${s.monto}</td>
                <td>{s.plazo} meses</td>
                <td>{(s.tasa * 100).toFixed(2)}%</td>
                <td>${s.cuota}</td>
                <td>{s.resultado}</td>
                <td>{s.fecha}</td>
              </tr>
            ))}
            {historial.length === 0 && (
              <tr>
                <td colSpan={6} style={{ textAlign: "center", color: "#888" }}>
                  No hay simulaciones previas.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
