import React, { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:8080";

export default function Simulacion() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  // Estado del formulario
  const [monto, setMonto] = useState("");
  const [plazo, setPlazo] = useState("");

  // Estado de la simulaci√≥n
  const [simulacion, setSimulacion] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Redirigir si no hay usuario
  React.useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Funci√≥n para simular el cr√©dito
  const handleSimular = async (e) => {
    e.preventDefault();
    setError(null);
    setSimulacion(null);

    // Validaciones del formulario
    if (!monto || !plazo) {
      setError("Por favor, completa todos los campos");
      return;
    }

    if (parseFloat(monto) <= 0 || parseInt(plazo) <= 0) {
      setError("Monto y plazo deben ser mayores a 0");
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(
        `${API_BASE}/simulacion/api/simular`,
        {
          monto: parseFloat(monto),
          plazo: parseInt(plazo),
        },
        {
          headers: {
            Authorization: `Bearer ${user?.token}`,
          },
        }
      );

      setSimulacion(response.data);
    } catch (err) {
      console.error("Error al simular:", err);
      setError(err.response?.data?.error || "Error al procesar la simulaci√≥n");
    } finally {
      setLoading(false);
    }
  };

