import React, { useContext, useState, useEffect, useCallback } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { crearSimulacion, obtenerHistorial, eliminarSimulacion } from "../api/simulacion";

export default function Simulacion() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  // Estado del formulario
  const [monto, setMonto] = useState("");
  const [plazo, setPlazo] = useState("");

  // Estado de la simulaci√≥n actual
  const [simulacion, setSimulacion] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Estado del historial
  const [historial, setHistorial] = useState([]);
  const [mostrarTodoHistorial, setMostrarTodoHistorial] = useState(false);
  const [loadingHistorial, setLoadingHistorial] = useState(false);

  // Funci√≥n para cargar historial (memoizada con useCallback)
  const cargarHistorial = useCallback(async (limit = null) => {
    if (!user?.token) return;

    setLoadingHistorial(true);
    try {
      const response = await obtenerHistorial(user.token, limit);
      setHistorial(response.data);
    } catch (err) {
      console.error("Error al cargar historial:", err);
    } finally {
      setLoadingHistorial(false);
    }
  }, [user?.token]);

  // Redirigir si no hay usuario y cargar historial inicial
  useEffect(() => {
    if (!user) {
      navigate("/login");
    } else {
      cargarHistorial(4); // Cargar las √∫ltimas 4 simulaciones
    }
  }, [user, navigate, cargarHistorial]);

  // Funci√≥n para simular el cr√©dito
  const handleSimular = async (e) => {
    e.preventDefault();
    setError(null);
    setSimulacion(null);

    // Validaciones del formulario
    if (!monto || !plazo) {
      setError("Por favor, completa todos los campos");
      return;
    }

    if (parseFloat(monto) <= 0 || parseInt(plazo) <= 0) {
      setError("Monto y plazo deben ser mayores a 0");
      return;
    }

    setLoading(true);

    try {
      const response = await crearSimulacion(
        {
          monto: parseFloat(monto),
          plazo: parseInt(plazo),
        },
        user.token
      );

      setSimulacion(response.data);
      // Recargar historial despu√©s de crear una nueva simulaci√≥n
      cargarHistorial(mostrarTodoHistorial ? null : 4);
    } catch (err) {
      console.error("Error al simular:", err);
      setError(err.response?.data?.error || "Error al procesar la simulaci√≥n");
    } finally {
      setLoading(false);
    }
  };

  // Funci√≥n para limpiar el formulario
  const handleLimpiar = () => {
    setMonto("");
    setPlazo("");
    setSimulacion(null);
    setError(null);
  };

  // Funci√≥n para eliminar una simulaci√≥n
  const handleEliminar = async (id) => {
    if (!window.confirm("¬øEst√°s seguro de que deseas eliminar esta simulaci√≥n?")) {
      return;
    }

    try {
      await eliminarSimulacion(id, user.token);
      // Recargar historial
      cargarHistorial(mostrarTodoHistorial ? null : 4);
      // Si la simulaci√≥n eliminada es la actual, limpiarla
      if (simulacion && simulacion.id === id) {
        setSimulacion(null);
      }
    } catch (err) {
      console.error("Error al eliminar:", err);
      alert("Error al eliminar la simulaci√≥n");
    }
  };

  // Funci√≥n para ver m√°s/menos historial
  const handleVerMas = () => {
    if (mostrarTodoHistorial) {
      cargarHistorial(4);
      setMostrarTodoHistorial(false);
    } else {
      cargarHistorial(null);
      setMostrarTodoHistorial(true);
    }
  };

  // Funci√≥n para seleccionar una simulaci√≥n del historial
  const handleSeleccionarSimulacion = (sim) => {
    setSimulacion(sim);
    setMonto(sim.monto.toString());
    setPlazo(sim.plazo.toString());
    // Scroll hacia arriba para ver la simulaci√≥n
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat("es-CL", {
      style: "currency",
      currency: "CLP",
    }).format(value);
  };

  const formatPercent = (value) => {
    return `${(value * 100).toFixed(2)}%`;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("es-CL", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "32px" }}>
        üßÆ Simulador de Cr√©dito
      </h2>

      {/* Formulario de Simulaci√≥n */}
      <div
        style={{
          backgroundColor: "#f8f9fa",
          padding: "24px",
          borderRadius: "8px",
          marginBottom: "32px",
        }}
      >
        <form onSubmit={handleSimular}>
          <div style={{ marginBottom: "16px" }}>
            <label
              style={{
                display: "block",
                fontWeight: "bold",
                marginBottom: "8px",
              }}
            >
              Monto del Pr√©stamo ($)
            </label>
            <input
              type="number"
              value={monto}
              onChange={(e) => setMonto(e.target.value)}
              placeholder="Ej: 5000000"
              style={{
                width: "100%",
                padding: "12px",
                fontSize: "16px",
                borderRadius: "4px",
                border: "1px solid #ced4da",
              }}
            />
          </div>

          <div style={{ marginBottom: "24px" }}>
            <label
              style={{
                display: "block",
                fontWeight: "bold",
                marginBottom: "8px",
              }}
            >
              N√∫mero de Cuotas (meses)
            </label>
            <input
              type="number"
              value={plazo}
              onChange={(e) => setPlazo(e.target.value)}
              placeholder="Ej: 24"
              style={{
                width: "100%",
                padding: "12px",
                fontSize: "16px",
                borderRadius: "4px",
                border: "1px solid #ced4da",
              }}
            />
          </div>

          {error && (
            <div
              style={{
                backgroundColor: "#f8d7da",
                color: "#721c24",
                padding: "12px",
                borderRadius: "4px",
                marginBottom: "16px",
              }}
            >
              {error}
            </div>
          )}

          <div style={{ display: "flex", gap: "12px" }}>
            <button
              type="submit"
              disabled={loading}
              style={{
                flex: 1,
                padding: "12px 24px",
                fontSize: "16px",
                fontWeight: "bold",
                color: "white",
                backgroundColor: loading ? "#6c757d" : "#007bff",
                border: "none",
                borderRadius: "4px",
                cursor: loading ? "not-allowed" : "pointer",
              }}
            >
              {loading ? "Simulando..." : "Simular Cr√©dito"}
            </button>
            <button
              type="button"
              onClick={handleLimpiar}
              style={{
                padding: "12px 24px",
                fontSize: "16px",
                color: "#6c757d",
                backgroundColor: "white",
                border: "1px solid #ced4da",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Limpiar
            </button>
          </div>
        </form>
      </div>

      {/* Resultado de la Simulaci√≥n */}
      {simulacion && (
        <div style={{ marginBottom: "48px" }}>
          {/* Estado de la Simulaci√≥n */}
          <div
            style={{
              backgroundColor:
                simulacion.resultado === "aprobado" ? "#d4edda" : "#f8d7da",
              color:
                simulacion.resultado === "aprobado" ? "#155724" : "#721c24",
              padding: "16px",
              borderRadius: "8px",
              marginBottom: "24px",
              textAlign: "center",
              fontSize: "18px",
              fontWeight: "bold",
            }}
          >
            {simulacion.resultado === "aprobado"
              ? "‚úÖ Simulaci√≥n Aprobada"
              : "‚ùå Simulaci√≥n Rechazada"}
          </div>

          {/* Cuota Mensual Destacada */}
          <div
            style={{
              backgroundColor: "#e7f3ff",
              padding: "24px",
              borderRadius: "8px",
              marginBottom: "24px",
              textAlign: "center",
            }}
          >
            <div style={{ fontSize: "14px", color: "#666", marginBottom: "8px" }}>
              Tu cuota mensual ser√°:
            </div>
            <div
              style={{ fontSize: "36px", fontWeight: "bold", color: "#007bff" }}
            >
              {formatCurrency(simulacion.cuotaMensual)}
            </div>
            <div style={{ fontSize: "14px", color: "#666", marginTop: "8px" }}>
              por {simulacion.plazo} meses
            </div>
          </div>

          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "24px" }}>
            {/* Composici√≥n del Cr√©dito */}
            <div
              style={{
                backgroundColor: "white",
                border: "1px solid #dee2e6",
                borderRadius: "8px",
                padding: "20px",
              }}
            >
              <h3 style={{ marginTop: 0, marginBottom: "16px", color: "#495057" }}>
                üí∞ Composici√≥n del Cr√©dito
              </h3>
              <div style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Monto Total del Cr√©dito:</span>
                  <strong>{formatCurrency(simulacion.montoTotal)}</strong>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Monto Solicitado:</span>
                  <strong>{formatCurrency(simulacion.monto)}</strong>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Monto L√≠quido (recibes):</span>
                  <strong style={{ color: "#28a745" }}>
                    {formatCurrency(simulacion.montoLiquido)}
                  </strong>
                </div>
                <hr style={{ margin: "8px 0", border: "none", borderTop: "1px solid #dee2e6" }} />
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Intereses Totales:</span>
                  <strong style={{ color: "#dc3545" }}>
                    {formatCurrency(simulacion.interesesTotales)}
                  </strong>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Gastos Operacionales:</span>
                  <strong>{formatCurrency(simulacion.gastosOperacionales)}</strong>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Comisi√≥n de Apertura:</span>
                  <strong>{formatCurrency(simulacion.comisionApertura)}</strong>
                </div>
              </div>
            </div>

            {/* Tasas e Informaci√≥n */}
            <div
              style={{
                backgroundColor: "white",
                border: "1px solid #dee2e6",
                borderRadius: "8px",
                padding: "20px",
              }}
            >
              <h3 style={{ marginTop: 0, marginBottom: "16px", color: "#495057" }}>
                üìä Tasas e Informaci√≥n
              </h3>
              <div style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>Tasa Base (anual):</span>
                  <strong>{formatPercent(simulacion.tasaBase)}</strong>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <span style={{ color: "#6c757d" }}>CAE (Carga Anual Equiv.):</span>
                  <strong style={{ color: "#ff6b6b" }}>
                    {formatPercent(simulacion.cae)}
                  </strong>
                </div>
                <hr style={{ margin: "8px 0", border: "none", borderTop: "1px solid #dee2e6" }} />
                <div
                  style={{
                    backgroundColor: "#fff3cd",
                    padding: "12px",
                    borderRadius: "4px",
                    fontSize: "13px",
                    color: "#856404",
                  }}
                >
                  <strong>‚ÑπÔ∏è Nota:</strong>
                  <br />
                  La CAE incluye todos los costos asociados al cr√©dito y permite
                  comparar entre diferentes opciones de financiamiento.
                </div>
              </div>
            </div>
          </div>

          {/* Informaci√≥n adicional */}
          <div
            style={{
              marginTop: "24px",
              padding: "16px",
              backgroundColor: "#f8f9fa",
              borderRadius: "8px",
              fontSize: "14px",
              color: "#6c757d",
            }}
          >
            <strong>üìå Informaci√≥n importante:</strong>
            <ul style={{ marginTop: "8px", marginBottom: 0 }}>
              <li>Esta simulaci√≥n es referencial y no constituye una oferta de cr√©dito.</li>
              <li>Los valores pueden variar seg√∫n tu evaluaci√≥n crediticia.</li>
              <li>El monto l√≠quido es lo que efectivamente recibes despu√©s de descontar los costos.</li>
            </ul>
          </div>
        </div>
      )}

      {/* Historial de Simulaciones */}
      <div style={{ marginTop: "48px" }}>
        <h3 style={{ marginBottom: "24px", color: "#495057" }}>
          üìã Historial de Simulaciones
        </h3>

        {loadingHistorial ? (
          <div style={{ textAlign: "center", padding: "32px", color: "#6c757d" }}>
            Cargando historial...
          </div>
        ) : historial.length === 0 ? (
          <div
            style={{
              textAlign: "center",
              padding: "48px",
              backgroundColor: "#f8f9fa",
              borderRadius: "8px",
              color: "#6c757d",
            }}
          >
            <p style={{ fontSize: "18px", marginBottom: "8px" }}>
              No tienes simulaciones previas
            </p>
            <p style={{ fontSize: "14px" }}>
              Crea tu primera simulaci√≥n usando el formulario de arriba
            </p>
          </div>
        ) : (
          <>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "16px" }}>
              {historial.map((sim) => (
                <div
                  key={sim.id}
                  style={{
                    backgroundColor: "white",
                    border: "1px solid #dee2e6",
                    borderRadius: "8px",
                    padding: "16px",
                    cursor: "pointer",
                    transition: "all 0.2s",
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.boxShadow = "0 4px 8px rgba(0,0,0,0.1)";
                    e.currentTarget.style.transform = "translateY(-2px)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.boxShadow = "none";
                    e.currentTarget.style.transform = "translateY(0)";
                  }}
                  onClick={() => handleSeleccionarSimulacion(sim)}
                >
                  <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "12px" }}>
                    <span
                      style={{
                        fontSize: "12px",
                        padding: "4px 8px",
                        borderRadius: "4px",
                        backgroundColor: sim.resultado === "aprobado" ? "#d4edda" : "#f8d7da",
                        color: sim.resultado === "aprobado" ? "#155724" : "#721c24",
                        fontWeight: "bold",
                      }}
                    >
                      {sim.resultado.toUpperCase()}
                    </span>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEliminar(sim.id);
                      }}
                      style={{
                        backgroundColor: "transparent",
                        border: "none",
                        color: "#dc3545",
                        cursor: "pointer",
                        fontSize: "18px",
                        padding: "4px 8px",
                      }}
                      title="Eliminar simulaci√≥n"
                    >
                      üóëÔ∏è
                    </button>
                  </div>

                  <div style={{ marginBottom: "12px" }}>
                    <div style={{ fontSize: "24px", fontWeight: "bold", color: "#007bff" }}>
                      {formatCurrency(sim.cuotaMensual)}
                    </div>
                    <div style={{ fontSize: "12px", color: "#6c757d" }}>
                      Cuota mensual
                    </div>
                  </div>

                  <div style={{ fontSize: "14px", color: "#6c757d", marginBottom: "8px" }}>
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
                      <span>Monto:</span>
                      <strong>{formatCurrency(sim.monto)}</strong>
                    </div>
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
                      <span>Plazo:</span>
                      <strong>{sim.plazo} meses</strong>
                    </div>
                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                      <span>CAE:</span>
                      <strong>{formatPercent(sim.cae)}</strong>
                    </div>
                  </div>

                  <div
                    style={{
                      fontSize: "11px",
                      color: "#999",
                      marginTop: "12px",
                      paddingTop: "12px",
                      borderTop: "1px solid #eee",
                    }}
                  >
                    {formatDate(sim.fecha)}
                  </div>
                </div>
              ))}
            </div>

            {/* Bot√≥n Ver M√°s / Ver Menos */}
            {historial.length >= 4 && (
              <div style={{ textAlign: "center", marginTop: "24px" }}>
                <button
                  onClick={handleVerMas}
                  style={{
                    padding: "12px 24px",
                    fontSize: "16px",
                    color: "#007bff",
                    backgroundColor: "white",
                    border: "1px solid #007bff",
                    borderRadius: "4px",
                    cursor: "pointer",
                    fontWeight: "bold",
                  }}
                >
                  {mostrarTodoHistorial ? "Ver Menos ‚ñ≤" : "Ver M√°s ‚ñº"}
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}
