/**
 * Módulo de Cálculos Financieros
 * 
 * Funciones puras para cálculos de crédito siguiendo el sistema francés.
 * No dependen de React ni del DOM, son completamente reutilizables.
 */

/**
 * Calcula la cuota mensual usando el sistema francés de amortización
 * @param {number} monto - Monto del crédito
 * @param {number} tasaMensual - Tasa de interés mensual (decimal, ej: 0.015 para 1.5%)
 * @param {number} plazo - Número de cuotas (meses)
 * @returns {number} Cuota mensual redondeada
 */
export const calcularCuotaMensual = (monto, tasaMensual, plazo) => {
  if (!monto || !plazo || monto <= 0 || plazo <= 0 || tasaMensual < 0) {
    return 0;
  }

  // Fórmula del sistema francés: C = M * i / (1 - (1+i)^-n)
  const cuota = monto * tasaMensual / (1 - Math.pow(1 + tasaMensual, -plazo));
  return Math.round(cuota);
};

/**
 * Calcula la Carga Anual Equivalente (CAE)
 * @param {number} tasaBaseAnual - Tasa base anual (decimal)
 * @param {number} gastosOperacionales - Gastos operacionales totales
 * @param {number} comisionApertura - Comisión de apertura total
 * @param {number} monto - Monto del crédito
 * @returns {number} CAE como decimal con 6 decimales
 */
export const calcularCAE = (tasaBaseAnual, gastosOperacionales, comisionApertura, monto) => {
  if (!monto || monto <= 0) return 0;
  const costosAdicionales = (gastosOperacionales + comisionApertura) / monto;
  return parseFloat((tasaBaseAnual + costosAdicionales).toFixed(6));
};

/**
 * Calcula gastos y montos derivados del crédito
 * @param {number} monto - Monto solicitado
 * @param {object} config - Configuración con tasas y porcentajes
 * @returns {object} Objeto con todos los montos calculados
 */
export const calcularMontos = (monto, config) => {
  const m = Number(monto);
  if (!m || m <= 0 || !config) {
    return null;
  }
  const gastosOperacionales = Math.round(m * config.gastosOperacionalesPorcentaje);
  const comisionApertura = Math.round(m * config.comisionApertura);
  const montoLiquido = Math.round(m - gastosOperacionales - comisionApertura);
  return {
    gastosOperacionales,
    comisionApertura,
    montoLiquido
  };
};

/**
 * Calcula una simulación completa de crédito
 * @param {number} monto - Monto solicitado
 * @param {number} plazo - Plazo en meses
 * @param {object} config - Configuración del sistema
 * @returns {object|null} Objeto con todos los datos de la simulación
 */
export const calcularSimulacion = (monto, plazo, config) => {
  const m = Number(monto);
  const p = Number(plazo);
  if (!m || !p || m <= 0 || p <= 0 || !config) {
    return null;
  }

  // Calcular montos
  const montos = calcularMontos(m, config);
  if (!montos) return null;

  // Calcular cuota mensual
  const tasaMensual = config.tasaBaseAnual / 12;
  const cuotaMensual = calcularCuotaMensual(m, tasaMensual, p);

  // Calcular totales
  const montoTotal = Math.round(cuotaMensual * p);
  const interesesTotales = Math.round(montoTotal - m);

  // Calcular CAE
  const cae = calcularCAE(config.tasaBaseAnual, montos.gastosOperacionales, montos.comisionApertura, m);

  // Determinar resultado
  const resultado = m >= config.montoMinimo && m <= config.montoMaximo && p >= config.plazoMinimo && p <= config.plazoMaximo ? 'aprobado' : 'rechazado';
  return {
    monto: m,
    plazo: p,
    tasaBase: config.tasaBaseAnual,
    cae,
    cuotaMensual,
    montoTotal,
    montoLiquido: montos.montoLiquido,
    interesesTotales,
    gastosOperacionales: montos.gastosOperacionales,
    comisionApertura: montos.comisionApertura,
    resultado
  };
};

/**
 * Genera la tabla de amortización mes a mes
 * @param {number} monto - Monto del crédito
 * @param {number} cuotaMensual - Cuota mensual fija
 * @param {number} tasaMensual - Tasa de interés mensual (decimal)
 * @param {number} plazo - Número de cuotas
 * @returns {Array} Array de objetos con el detalle de cada mes
 */
export const generarTablaAmortizacion = (monto, cuotaMensual, tasaMensual, plazo) => {
  const tabla = [];
  let saldoInsoluto = Number(monto);
  for (let mes = 1; mes <= plazo; mes++) {
    const interes = Math.round(saldoInsoluto * tasaMensual);
    const amortizacion = Math.round(cuotaMensual - interes);
    saldoInsoluto = Math.round(saldoInsoluto - amortizacion);

    // Ajuste del último mes para asegurar saldo cero
    if (mes === plazo && saldoInsoluto !== 0) {
      const ajuste = saldoInsoluto;
      saldoInsoluto = 0;
      tabla.push({
        mes,
        cuota: cuotaMensual + ajuste,
        interes,
        amortizacion: amortizacion + ajuste,
        saldo: 0
      });
    } else {
      tabla.push({
        mes,
        cuota: cuotaMensual,
        interes,
        amortizacion,
        saldo: Math.max(0, saldoInsoluto)
      });
    }
  }
  return tabla;
};

/**
 * Valida si un monto está dentro de los límites permitidos
 * @param {number} monto - Monto a validar
 * @param {object} config - Configuración con límites
 * @returns {object} { valid: boolean, message: string }
 */
export const validarMonto = (monto, config) => {
  const m = Number(monto);
  if (!m || isNaN(m)) {
    return {
      valid: false,
      message: 'El monto debe ser un número válido'
    };
  }
  if (m < config.montoMinimo) {
    return {
      valid: false,
      message: `El monto mínimo es ${config.montoMinimo.toLocaleString('es-CL', {
        style: 'currency',
        currency: 'CLP'
      })}`
    };
  }
  if (m > config.montoMaximo) {
    return {
      valid: false,
      message: `El monto máximo es ${config.montoMaximo.toLocaleString('es-CL', {
        style: 'currency',
        currency: 'CLP'
      })}`
    };
  }
  return {
    valid: true,
    message: ''
  };
};

/**
 * Valida si un plazo está dentro de los límites permitidos
 * @param {number} plazo - Plazo a validar en meses
 * @param {object} config - Configuración con límites
 * @returns {object} { valid: boolean, message: string }
 */
export const validarPlazo = (plazo, config) => {
  const p = Number(plazo);
  if (!p || isNaN(p)) {
    return {
      valid: false,
      message: 'El plazo debe ser un número válido'
    };
  }
  if (p < config.plazoMinimo) {
    return {
      valid: false,
      message: `El plazo mínimo es ${config.plazoMinimo} meses`
    };
  }
  if (p > config.plazoMaximo) {
    return {
      valid: false,
      message: `El plazo máximo es ${config.plazoMaximo} meses`
    };
  }
  return {
    valid: true,
    message: ''
  };
};

/**
 * Valida una simulación completa
 * @param {number} monto - Monto del crédito
 * @param {number} plazo - Plazo en meses
 * @param {object} config - Configuración del sistema
 * @returns {object} { valid: boolean, errors: Array<string> }
 */
export const validarSimulacion = (monto, plazo, config) => {
  const errors = [];
  const validacionMonto = validarMonto(monto, config);
  if (!validacionMonto.valid) {
    errors.push(validacionMonto.message);
  }
  const validacionPlazo = validarPlazo(plazo, config);
  if (!validacionPlazo.valid) {
    errors.push(validacionPlazo.message);
  }
  return {
    valid: errors.length === 0,
    errors
  };
};

