// src/pages/Register.js
import React, { useState } from "react";
import { Container, TextField, Button, Typography, Box, Alert } from "@mui/material";
import { register as registerUser } from "../api/auth";

export default function Register() {
  const [form, setForm] = useState({ username: "", password: "" });
  const [message, setMessage] = useState(null);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);

    try {
      const res = await registerUser(form);
      setMessage({ type: "success", text: res.data || "Usuario creado" });
    } catch (err) {
      const msg = err?.response?.data?.error || err?.message || "Error al registrarse";
      setMessage({ type: "error", text: msg });
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 10 }}>
      <Typography variant="h4" gutterBottom>
        Crear una cuenta (usuarios del negocio)
      </Typography>
      <Typography variant="body2" color="text.secondary">
        Nota: las cuentas de super usuario son gestionadas por el administrador.
      </Typography>
      <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
        <TextField
          fullWidth
          label="Usuario"
          name="username"
          margin="normal"
          value={form.username}
          onChange={handleChange}
        />
        <TextField
          fullWidth
          label="ContraseÃ±a"
          name="password"
          type="password"
          margin="normal"
          value={form.password}
          onChange={handleChange}
        />
        <Button type="submit" fullWidth variant="contained" sx={{ mt: 2 }}>
          Registrarse
        </Button>
      </Box>
      {message && (
        <Alert sx={{ mt: 3 }} severity={message.type}>
          {message.text}
        </Alert>
      )}
    </Container>
  );
}
