// src/pages/RegisterSuccess.js
import React, { useEffect, useState } from "react";
import { Container, Typography, Button, Box } from "@mui/material";
import { useNavigate, useLocation } from "react-router-dom";

export default function RegisterSuccess() {
  const navigate = useNavigate();
  const location = useLocation();
  const seconds = 120; // tiempo antes de redirigir
  const [remaining, setRemaining] = useState(seconds);

  useEffect(() => {
    const interval = setInterval(() => {
      setRemaining((r) => {
        if (r <= 1) {
          clearInterval(interval);
          navigate("/login", { replace: true });
          return 0;
        }
        return r - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [navigate]);

  const email = location.state?.email;
  const username = location.state?.username;

  return (
    <Container maxWidth="sm" sx={{ mt: 10 }}>
      <Typography variant="h4" gutterBottom>
        Registro completado
      </Typography>
      <Box sx={{ mt: 2 }}>
        <Typography variant="body1">
          Tu cuenta se cre칩 correctamente{username ? `, ${username}` : ""}.{" "}
          {email ? `Verifica si recibiste un correo en ${email}.` : ""}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          Ser치s redirigido a Iniciar sesi칩n en {remaining} segundos.
        </Typography>
        <Button variant="contained" sx={{ mt: 3 }} onClick={() => navigate("/login", { replace: true })}>
          Ir a Iniciar sesi칩n ahora
        </Button>
      </Box>
    </Container>
  );
}
