import React from "react";
import {
  Box,
  Button,
  Card,
  Container,
  Grid,
  Paper,
  Stack,
  Typography,
  Chip,
  Divider,
} from "@mui/material";
import { alpha } from "@mui/material/styles";
import VerifiedIcon from "@mui/icons-material/Verified";
import LockIcon from "@mui/icons-material/Lock";
import SpeedIcon from "@mui/icons-material/Speed";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import QueryStatsIcon from "@mui/icons-material/QueryStats";
import SmartphoneIcon from "@mui/icons-material/Smartphone";
import BoltIcon from "@mui/icons-material/Bolt";
import FactCheckIcon from "@mui/icons-material/FactCheck";
import PaidIcon from "@mui/icons-material/Paid";
import SecurityIcon from "@mui/icons-material/Security";
import TrendingUpIcon from "@mui/icons-material/TrendingUp";
import SupportAgentIcon from "@mui/icons-material/SupportAgent";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";
import { useNavigate } from "react-router-dom";

export default function Landing() {
  const navigate = useNavigate();
  const handleSimular = () => navigate("/login");
  const handleSolicitar = () => navigate("/register");

  const features = [
    {
      title: "Simulación Inteligente",
      text: "Conoce tu cuota y plazos en segundos con una simulación clara y transparente.",
      icon: <QueryStatsIcon fontSize="medium" />,
    },
    {
      title: "Solicitud 100% Digital",
      text: "Completa tu solicitud desde cualquier dispositivo, sin visitas a sucursal.",
      icon: <SmartphoneIcon fontSize="medium" />,
    },
    {
      title: "Evaluación Express",
      text: "Procesos ágiles para darte una respuesta rápida y oportuna.",
      icon: <BoltIcon fontSize="medium" />,
    },
    {
      title: "Firma Digital Avanzada",
      text: "Documentación firmada digitalmente, con validez legal y máxima seguridad.",
      icon: <FactCheckIcon fontSize="medium" />,
    },
    {
      title: "Desembolso Inmediato",
      text: "Una vez aprobado, recibe tu dinero en tu cuenta en minutos.",
      icon: <PaidIcon fontSize="medium" />,
    },
    {
      title: "Seguridad y Transparencia",
      text: "Tu información protegida, tu decisión informada. Sin letras chicas, sin sorpresas.",
      icon: <SecurityIcon fontSize="medium" />,
    },
  ];

  const extraFeatures = [
    {
      title: "Mejores Tasas",
      text: "Accede a tasas competitivas que se ajustan a tu perfil.",
      icon: <TrendingUpIcon fontSize="medium" />,
    },
    {
      title: "Soporte Personalizado",
      text: "Nuestro equipo te acompaña en cada paso del proceso.",
      icon: <SupportAgentIcon fontSize="medium" />,
    },
    {
      title: "Clientes Satisfechos",
      text: "Miles de personas ya confiaron en nosotros. ¡Únete tú también!",
      icon: <EmojiEventsIcon fontSize="medium" />,
    },
  ];

  return (
    <Box>
      {/* HERO Section */}
      <Box
        component="section"
        sx={{
          position: "relative",
          minHeight: { xs: "60vh", md: "70vh" },
          display: "flex",
          alignItems: "center",
          backgroundImage:
            "url(https://images.unsplash.com/photo-1522202176988-66273c2fd55f?q=80&w=1600&auto=format&fit=crop)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background: (t) =>
              t.palette.mode === "dark"
                ? `linear-gradient(180deg, rgba(0,0,0,.55), rgba(0,0,0,.55))`
                : `linear-gradient(180deg, rgba(255,255,255,.1), rgba(0,0,0,.35))`,
          }}
        />
        <Container maxWidth="lg" sx={{ position: "relative" }}>
          <Box sx={{ maxWidth: { xs: "100%", md: 760 } }}>
            <Typography variant="overline" sx={{ letterSpacing: 1.2, fontWeight: 700, color: "#fff", opacity: 0.9 }}>
              POGWARE
            </Typography>
            <Typography component="h1" variant="h2" fontWeight={900} gutterBottom sx={{ color: "#fff" }}>
              Tu Préstamo Digital, Rápido y Sin Complicaciones
            </Typography>
            <Typography variant="h6" sx={{ color: "#e6e6e6" }}>
              Simula, solicita y recibe tu dinero en minutos. Todo desde tu celular.
            </Typography>
            <Button variant="contained" size="large" sx={{ mt: 3 }} onClick={handleSolicitar}>
              Solicita Ahora
            </Button>
          </Box>
        </Container>
      </Box>

      {/* Trust Bar */}
      <Box sx={{ py: 3 }}>
        <Container maxWidth="lg">
          <Paper elevation={0} sx={{ py: 2, px: 3, backgroundColor: alpha("#1976d2", 0.05), borderRadius: 2 }}>
            <Typography variant="subtitle2" align="center" gutterBottom>
              Confianza y Seguridad Garantizadas
            </Typography>
            <Stack direction="row" spacing={1} useFlexGap flexWrap="wrap" justifyContent="center">
              <Chip icon={<VerifiedIcon />} label="Entidad confiable" variant="outlined" />
              <Chip icon={<LockIcon />} label="Seguridad de datos" variant="outlined" />
              <Chip icon={<SpeedIcon />} label="Respuesta rápida" variant="outlined" />
              <Chip icon={<PhoneIphoneIcon />} label="100% móvil" variant="outlined" />
            </Stack>
          </Paper>
        </Container>
      </Box>

      {/* Features Section */}
      <Box sx={{ py: 6 }}>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            {features.map((feature, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card
                  elevation={3}
                  sx={{
                    p: 3,
                    height: "100%",
                    transition: "transform 0.3s",
                    "&:hover": { transform: "scale(1.03)" },
                  }}
                >
                  <Stack spacing={2}>
                    <Box color="primary.main">{feature.icon}</Box>
                    <Typography variant="h6" fontWeight={700}>
                      {feature.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {feature.text}
                    </Typography>
                  </Stack>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Extra Features Section */}
      <Box sx={{ py: 6, backgroundColor: alpha("#1976d2", 0.03) }}>
        <Container maxWidth="lg">
          <Typography variant="h5" fontWeight={800} align="center" gutterBottom>
            ¿Por qué elegirnos?
          </Typography>
          <Grid container spacing={4}>
            {extraFeatures.map((feature, index) => (
              <Grid item xs={12} sm={4} key={index}>
                <Card elevation={2} sx={{ p: 3, height: "100%" }}>
                  <Stack spacing={2} alignItems="center">
                    <Box color="primary.main">{feature.icon}</Box>
                    <Typography variant="subtitle1" fontWeight={700} align="center">
                      {feature.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" align="center">
                      {feature.text}
                    </Typography>
                  </Stack>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* CTA Final */}
      <Box component="section" sx={{ py: { xs: 6, md: 8 }, background: (t) => (t.palette.mode === "dark" ? t.palette.background.paper : "#f6f9ff") }}>
        <Container maxWidth="md">
          <Paper elevation={0} sx={{ p: { xs: 4, md: 6 }, textAlign: "center", borderRadius: 3, border: (t) => `1px solid ${t.palette.divider}` }}>
            <Typography variant="h5" fontWeight={800} gutterBottom>
              Listo para comenzar
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 3 }}>
              Da el siguiente paso y accede a tu Préstamo de Consumo 100% Digital.
            </Typography>
            <Stack direction={{ xs: "column", sm: "row" }} spacing={2} justifyContent="center">
              <Button size="large" variant="contained" startIcon={<PaidIcon />} onClick={handleSolicitar}>
                Solicita tu Crédito
              </Button>
              <Button size="large" variant="outlined" startIcon={<QueryStatsIcon />} onClick={handleSimular}>
                Simula tu Crédito
              </Button>
            </Stack>
          </Paper>
        </Container>
      </Box>

      {/* Footer */}
      <Box component="footer" sx={{ py: 4, borderTop: (t) => `1px solid ${t.palette.divider}` }}>
        <Container maxWidth="lg">
          <Typography variant="body2" color="text.secondary" align="center">
            © {new Date().getFullYear()} POGWARE · Créditos Financieros
          </Typography>
        </Container>
      </Box>
    </Box>
  );
}
