import React, { useContext, useState, useEffect, useCallback } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { crearSimulacion, obtenerHistorial, eliminarSimulacion } from "../api/simulacion";

export default function Simulacion() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  // Estado del formulario
  const [monto, setMonto] = useState("");
  const [plazo, setPlazo] = useState("");

  // Estado de la simulaciÃ³n actual
  const [simulacion, setSimulacion] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Estado del historial
  const [historial, setHistorial] = useState([]);
  const [mostrarTodoHistorial, setMostrarTodoHistorial] = useState(false);
  const [loadingHistorial, setLoadingHistorial] = useState(false);

  // Estado para vista previa (cÃ¡lculo local sin guardar)
  const [vistaPrevia, setVistaPrevia] = useState(null);
  const [mostrarAmortizacion, setMostrarAmortizacion] = useState(false);

  // FunciÃ³n para cargar historial (memoizada con useCallback)
  const cargarHistorial = useCallback(async (limit = null) => {
    if (!user?.token) return;

    setLoadingHistorial(true);
    try {
      const response = await obtenerHistorial(user.token, limit);
      setHistorial(response.data);
    } catch (err) {
      console.error("Error al cargar historial:", err);
    } finally {
      setLoadingHistorial(false);
    }
  }, [user?.token]);

  // Redirigir si no hay usuario y cargar historial inicial
  useEffect(() => {
    if (!user) {
      navigate("/login");
    } else {
      cargarHistorial(4); // Cargar las Ãºltimas 4 simulaciones
    }
  }, [user, navigate, cargarHistorial]);

  // FunciÃ³n para simular el crÃ©dito
  const handleSimular = async (e) => {
    e.preventDefault();
    setError(null);
    setSimulacion(null);

    // Validaciones del formulario
    if (!monto || !plazo) {
      setError("Por favor, completa todos los campos");
      return;
    }

    if (parseFloat(monto) <= 0 || parseInt(plazo) <= 0) {
      setError("Monto y plazo deben ser mayores a 0");
      return;
    }

    setLoading(true);

    try {
      const response = await crearSimulacion(
        {
          monto: parseFloat(monto),
          plazo: parseInt(plazo),
        },
        user.token
      );

      setSimulacion(response.data);
      // Recargar historial despuÃ©s de crear una nueva simulaciÃ³n
      cargarHistorial(mostrarTodoHistorial ? null : 4);
    } catch (err) {
      console.error("Error al simular:", err);
      setError(err.response?.data?.error || "Error al procesar la simulaciÃ³n");
    } finally {
      setLoading(false);
    }
  };

  // FunciÃ³n para limpiar el formulario
  const handleLimpiar = () => {
    setMonto("");
    setPlazo("");
    setSimulacion(null);
    setError(null);
  };

  // FunciÃ³n para eliminar una simulaciÃ³n
  const handleEliminar = async (id) => {
    if (!window.confirm("Â¿EstÃ¡s seguro de que deseas eliminar esta simulaciÃ³n?")) {
      return;
    }

    try {
      await eliminarSimulacion(id, user.token);
      // Recargar historial
      cargarHistorial(mostrarTodoHistorial ? null : 4);
      // Si la simulaciÃ³n eliminada es la actual, limpiarla
      if (simulacion && simulacion.id === id) {
        setSimulacion(null);
      }
    } catch (err) {
      console.error("Error al eliminar:", err);
      alert("Error al eliminar la simulaciÃ³n");
    }
  };

  // FunciÃ³n para ver mÃ¡s/menos historial
  const handleVerMas = () => {
    if (mostrarTodoHistorial) {
      cargarHistorial(4);
      setMostrarTodoHistorial(false);
    } else {
      cargarHistorial(null);
      setMostrarTodoHistorial(true);
    }
  };

  // FunciÃ³n para seleccionar una simulaciÃ³n del historial
  const handleSeleccionarSimulacion = (sim) => {
    setSimulacion(sim);
    setMonto(sim.monto.toString());
    setPlazo(sim.plazo.toString());
    // Scroll hacia arriba para ver la simulaciÃ³n
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat("es-CL", {
      style: "currency",
      currency: "CLP",
    }).format(value);
  };

  const formatPercent = (value) => {
    return `${(value * 100).toFixed(2)}%`;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("es-CL", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  // FunciÃ³n para calcular vista previa localmente (sin guardar en BD)
  const calcularVistaPrevia = useCallback((montoInput, plazoInput) => {
    const m = parseFloat(montoInput);
    const p = parseInt(plazoInput);

    if (!m || !p || m <= 0 || p <= 0) {
      setVistaPrevia(null);
      return;
    }

    // ConfiguraciÃ³n (debe coincidir con el backend)
    const TASA_BASE_ANUAL = 0.20;
    const GASTOS_OP_PORCENTAJE = 0.03;
    const COMISION_APERTURA = 0.01;

    // CÃ¡lculos
    const gastosOp = m * GASTOS_OP_PORCENTAJE;
    const comisionAp = m * COMISION_APERTURA;
    const montoLiq = m - gastosOp - comisionAp;

    // Cuota mensual (sistema francÃ©s)
    const i = TASA_BASE_ANUAL / 12;
    const cuota = m * i / (1 - Math.pow(1 + i, -p));

    const montoTot = cuota * p;
    const intereses = montoTot - m;
    const cae = TASA_BASE_ANUAL + ((gastosOp + comisionAp) / m);
    const resultado = m >= 1000 && p >= 6 && p <= 60 ? 'aprobado' : 'rechazado';

    setVistaPrevia({
      monto: m,
      plazo: p,
      tasaBase: TASA_BASE_ANUAL,
      cae,
      cuotaMensual: cuota,
      montoTotal: montoTot,
      montoLiquido: montoLiq,
      interesesTotales: intereses,
      gastosOperacionales: gastosOp,
      comisionApertura: comisionAp,
      resultado,
    });
  }, []);

  // Actualizar vista previa cuando cambian monto o plazo
  useEffect(() => {
    calcularVistaPrevia(monto, plazo);
  }, [monto, plazo, calcularVistaPrevia]);

  // FunciÃ³n para generar tabla de amortizaciÃ³n
  const generarTablaAmortizacion = (sim) => {
    if (!sim) return [];

    const tabla = [];
    let saldo = sim.monto;
    const i = sim.tasaBase / 12;

    for (let mes = 1; mes <= sim.plazo; mes++) {
      const interes = saldo * i;
      const capital = sim.cuotaMensual - interes;
      saldo -= capital;

      tabla.push({
        mes,
        cuota: sim.cuotaMensual,
        interes,
        capital,
        saldo: Math.max(0, saldo), // Evitar saldos negativos por redondeo
      });
    }

    return tabla;
  };

  return (
    <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "32px" }}>
        ðŸ§® Simulador de CrÃ©dito
      </h2>

      {/* Formulario de SimulaciÃ³n */}
      <div
        style={{
          backgroundColor: "#f8f9fa",
          padding: "24px",
          borderRadius: "8px",
          marginBottom: "32px",
        }}
      >
        <form onSubmit={handleSimular}>
          <div style={{ marginBottom: "16px" }}>
            <label
              style={{
                display: "block",
                fontWeight: "bold",
                marginBottom: "8px",
              }}
            >
              Monto del PrÃ©stamo ($)
            </label>
            <input
              type="number"
              value={monto}
              onChange={(e) => setMonto(e.target.value)}
              placeholder="Ej: 5000000"
              style={{
                width: "100%",
                padding: "12px",
                fontSize: "16px",
                borderRadius: "4px",
                border: "1px solid #ced4da",
              }}
            />
          </div>

          <div style={{ marginBottom: "24px" }}>
            <label
              style={{
                display: "block",
                fontWeight: "bold",
                marginBottom: "8px",
              }}
            >
              NÃºmero de Cuotas (meses)
            </label>
            <input
              type="number"
              value={plazo}
              onChange={(e) => setPlazo(e.target.value)}
              placeholder="Ej: 24"
              style={{
                width: "100%",
                padding: "12px",
                fontSize: "16px",
                borderRadius: "4px",
                border: "1px solid #ced4da",
              }}
            />
          </div>

          {error && (
            <div
              style={{
                backgroundColor: "#f8d7da",
                color: "#721c24",
                padding: "12px",
                borderRadius: "4px",
                marginBottom: "16px",
              }}
            >
              {error}
            </div>
          )}

          <div style={{ display: "flex", gap: "12px" }}>
            <button
              type="submit"
              disabled={loading}
              style={{
                flex: 1,
                padding: "12px 24px",
                fontSize: "16px",
                fontWeight: "bold",
                color: "white",
                backgroundColor: loading ? "#6c757d" : "#007bff",
                border: "none",
                borderRadius: "4px",
                cursor: loading ? "not-allowed" : "pointer",
              }}
            >
              {loading ? "Simulando..." : "Simular CrÃ©dito"}
            </button>
            <button
              type="button"
              onClick={handleLimpiar}
              style={{
                padding: "12px 24px",
                fontSize: "16px",
                color: "#6c757d",
                backgroundColor: "white",
                border: "1px solid #ced4da",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Limpiar
            </button>
          </div>
        </form>
      </div>

      {/* Resultado de la SimulaciÃ³n */}
      {simulacion && (
        <div style={{ marginBottom: "48px" }}>
          {/* Estado de la SimulaciÃ³n */}
          <div
            style={{
              backgroundColor:
                simulacion.resultado === "aprobado" ? "#d4edda" : "#f8d7da",
              color:
                simulacion.resultado === "aprobado" ? "#155724" : "#721c24",
              padding: "16px",
              borderRadius: "8px",
              marginBottom: "24px",
              textAlign: "center",
              fontSize: "18px",
              fontWeight: "bold",
            }}
          >
            {simulacion.resultado === "aprobado"
              ? "âœ… SimulaciÃ³n Aprobada"
              : "âŒ SimulaciÃ³n Rechazada"}
          </div>

          {/* Cuota Mensual Destacada */}
          <div
            style={{
              backgroundColor: "#e7f3ff",
              padding: "24px",
              borderRadius: "8px",
              marginBottom: "24px",
              textAlign: "center",
            }}
          >
            <div style={{ fontSize: "14px", color: "#666", marginBottom: "8px" }}>
              Tu cuota mensual serÃ¡:
            </div>
            <div
              style={{ fontSize: "36px", fontWeight: "bold", color: "#007bff" }}
            >
              {formatCurrency(simulacion.cuotaMensual)}
            </div>
            <div style={{ fontSize: "14px", color: "#666", marginTop: "8px" }}>
              por {simulacion.plazo} meses
