import React, { useState, useContext } from "react";
import { Link as RouterLink, useNavigate } from "react-router-dom";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import MenuIcon from "@mui/icons-material/Menu";
import AccountCircle from "@mui/icons-material/AccountCircle";
import DarkModeIcon from "@mui/icons-material/DarkMode";
import LightModeIcon from "@mui/icons-material/LightMode";
import Tooltip from "@mui/material/Tooltip";
import { ThemeModeContext } from "../context/ThemeContext";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const { mode, toggleColorMode } = useContext(ThemeModeContext);
  const navigate = useNavigate();

  // Menú de cuenta (usuario autenticado)
  const [anchorElAccount, setAnchorElAccount] = useState(null);
  const openAccount = Boolean(anchorElAccount);
  const handleAccountOpen = (e) => setAnchorElAccount(e.currentTarget);
  const handleAccountClose = () => setAnchorElAccount(null);

  // Menú mobile (hamburger)
  const [anchorElMobile, setAnchorElMobile] = useState(null);
  const openMobile = Boolean(anchorElMobile);
  const handleMobileOpen = (e) => setAnchorElMobile(e.currentTarget);
  const handleMobileClose = () => setAnchorElMobile(null);

  const handleLogout = () => {
    logout();
    handleAccountClose();
    handleMobileClose();
    navigate("/");
  };

  // Si el token incluye el role en el objeto user, use user.role. En el ejemplo del contexto
  // user es { token } — si guardas decoded user deberías usar user.role. Aquí lo intentamos leer.
  const role = user && user.role ? user.role : null;

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography
          variant="h6"
          component={RouterLink}
          to="/"
          sx={{ color: "inherit", textDecoration: "none", flexGrow: 1 }}
        >
          MiApp Crédito
        </Typography>

        {/* menú para pantallas grandes */}
        <Box sx={{ display: { xs: "none", md: "flex" }, gap: 1, alignItems: "center" }}>
          {user && (
            <Button component={RouterLink} to="/dashboard" color="inherit">
              Dashboard
            </Button>
          )}

          {/* Eliminado botón Campañas por ahora */}

          {user && role === "admin" && (
            <Button component={RouterLink} to="/admin" color="inherit">
              Admin
            </Button>
          )}
          {user ? (
            // Botón visible para cerrar sesión (desktop)
            <Button color="inherit" onClick={handleLogout}>
              Cerrar sesión
            </Button>
          ) : (
            // si no está autenticado, mostrar login/register
            <>
              <Button component={RouterLink} to="/login" color="inherit">
                Iniciar sesión
              </Button>
              <Button component={RouterLink} to="/register" color="inherit">
                Registrarse
              </Button>
            </>
          )}

          {/* Toggle modo oscuro/claro (desktop) */}
          <Tooltip title={`Cambiar a modo ${mode === "dark" ? "claro" : "oscuro"}`}>
            <IconButton color="inherit" onClick={toggleColorMode} aria-label="toggle color mode">
              {mode === "dark" ? <LightModeIcon /> : <DarkModeIcon />}
            </IconButton>
          </Tooltip>
        </Box>

        {/* si hay usuario muestra icono con dropdown (desktop) */}
        {user && (
          <IconButton
            sx={{ display: { xs: "none", md: "flex" } }}
            size="large"
            edge="end"
            color="inherit"
            onClick={handleAccountOpen}
            aria-controls={openAccount ? "account-menu" : undefined}
            aria-haspopup="true"
          >
            <AccountCircle />
          </IconButton>
        )}

        <Menu
          id="account-menu"
          anchorEl={anchorElAccount}
          open={openAccount}
          onClose={handleAccountClose}
          anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
          transformOrigin={{ vertical: "top", horizontal: "right" }}
        >
          <MenuItem
            onClick={() => {
              handleAccountClose();
              navigate("/profile");
            }}
          >
            Perfil
          </MenuItem>
          {role === "admin" && (
            <MenuItem
              onClick={() => {
                handleAccountClose();
                navigate("/admin");
              }}
            >
              Panel Admin
            </MenuItem>
          )}
          <MenuItem onClick={handleLogout}>Cerrar sesión</MenuItem>
        </Menu>

        {/* menu para pantallas pequeñas */}
        <Box sx={{ display: { xs: "flex", md: "none" }, alignItems: "center", gap: 1 }}>
          {/* Toggle modo oscuro/claro (mobile) */}
          <IconButton color="inherit" onClick={toggleColorMode} aria-label="toggle color mode">
            {mode === "dark" ? <LightModeIcon /> : <DarkModeIcon />}
          </IconButton>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleMobileOpen}
          >
            <MenuIcon />
          </IconButton>
        </Box>

        {/* Contenido del menú móvil */}
        <Menu
          id="mobile-menu"
          anchorEl={anchorElMobile}
          open={openMobile}
          onClose={handleMobileClose}
          anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
          transformOrigin={{ vertical: "top", horizontal: "right" }}
        >
          {user ? (
            <>
              <MenuItem
                onClick={() => {
                  handleMobileClose();
                  navigate("/dashboard");
                }}
              >
                Dashboard
              </MenuItem>
              <MenuItem
                onClick={() => {
                  handleMobileClose();
                  navigate("/profile");
                }}
              >
                Perfil
              </MenuItem>
              {role === "admin" && (
                <MenuItem
                  onClick={() => {
                    handleMobileClose();
                    navigate("/admin");
                  }}
                >
                  Panel Admin
                </MenuItem>
              )}
              <MenuItem onClick={handleLogout}>Cerrar sesión</MenuItem>
            </>
          ) : (
            <>
              <MenuItem
                onClick={() => {
                  handleMobileClose();
                  navigate("/login");
                }}
              >
                Iniciar sesión
              </MenuItem>
              <MenuItem
                onClick={() => {
                  handleMobileClose();
                  navigate("/register");
                }}
              >
                Registrarse
              </MenuItem>
            </>
          )}
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
