// src/pages/Register.js
import React, { useState } from "react";
import { Container, TextField, Button, Typography, Box, Alert } from "@mui/material";
import { register as registerUser } from "../api/auth";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);

    if (!form.username || !form.email || !form.password) {
      setMessage({ type: "error", text: "Completa usuario, email y contraseña" });
      return;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) {
      setMessage({ type: "error", text: "Email inválido" });
      return;
    }
    if (form.password.length < 6) {
      setMessage({ type: "error", text: "La contraseña debe tener al menos 6 caracteres" });
      return;
    }

    try {
      // Navegar a vista de éxito y luego login
      navigate("/register/success", { replace: true, state: { username: form.username, email: form.email } });
      // Si prefieres mostrar el mensaje aquí además de navegar, puedes mantener esta línea:
      // setMessage({ type: "success", text: res.data || "Usuario creado" });
    } catch (err) {
      const msg = err?.response?.data?.error || err?.message || "Error al registrarse";
      setMessage({ type: "error", text: msg });
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 10 }}>
      <Typography variant="h4" gutterBottom>
        Crear una cuenta (usuarios del negocio)
      </Typography>
      <Typography variant="body2" color="text.secondary">
        Nota: las cuentas de super usuario son gestionadas por el administrador.
      </Typography>
      <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
        <TextField
          fullWidth
          label="Nombre de usuario"
          name="username"
          margin="normal"
          value={form.username}
          onChange={handleChange}
          required
        />
        <TextField
          fullWidth
          label="Email"
          name="email"
          type="email"
          margin="normal"
          value={form.email}
          onChange={handleChange}
          required
        />
        <TextField
          fullWidth
          label="Contraseña"
          name="password"
          type="password"
          margin="normal"
          value={form.password}
          onChange={handleChange}
          required
          helperText="Mínimo 6 caracteres"
        />
        <Button type="submit" fullWidth variant="contained" sx={{ mt: 2 }}>
          Registrarse
        </Button>
      </Box>
      {message && (
        <Alert sx={{ mt: 3 }} severity={message.type}>
          {message.text}
        </Alert>
      )}
    </Container>
  );
}
