/**
 * P√°gina: Simulacion
 * 
 * P√°gina principal de simulaci√≥n de cr√©ditos.
 * 
 * RESPONSABILIDADES:
 * - Coordinar componentes hijos
 * - Gestionar layout y estructura visual
 * - Manejar mensajes de error/√©xito
 * - NO contiene l√≥gica de negocio (delegada a hooks y utils)
 * - NO contiene c√°lculos (delegados a utils)
 * - NO contiene llamadas directas a API (delegadas a hooks)
 */

import React from 'react';
import { useSimulacion } from '../hooks/useSimulacion';
import { SimulacionForm } from '../components/SimulacionForm';
import { VistaPrevia } from '../components/VistaPrevia';
import { TablaAmortizacion } from '../components/TablaAmortizacion';
import { HistorialSimulaciones } from '../components/HistorialSimulaciones';

export default function Simulacion() {
  // Hook personalizado encapsula toda la l√≥gica
  const {
    // Estado de configuraci√≥n
    config,
    loadingConfig,

    // Estado del formulario
    monto,
    setMonto,
    plazo,
    setPlazo,

    // Estado de simulaci√≥n guardada
    loading,
    error,
    success,

    // Estado del historial
    historial,
    mostrarTodoHistorial,
    loadingHistorial,

    // Estado de vista previa
    vistaPrevia,
    mostrarAmortizacion,
    tablaAmortizacion,

    // Handlers
    handleSimular,
    handleEliminar,
    handleVerMas,
    toggleAmortizacion,
  } = useSimulacion();

  // ==========================================
  // ESTILOS
  // ==========================================
  const styles = {
    container: {
      maxWidth: '1400px',
      margin: '0 auto',
      padding: '32px 16px',
    },
    header: {
      textAlign: 'center',
      marginBottom: '32px',
    },
    title: {
      fontSize: '2.5rem',
      color: '#2c3e50',
      marginBottom: '8px',
    },
    subtitle: {
      fontSize: '1.1rem',
      color: '#7f8c8d',
    },
    alertError: {
      backgroundColor: '#f8d7da',
      color: '#721c24',
      padding: '16px',
      borderRadius: '8px',
      marginBottom: '24px',
      border: '1px solid #f5c6cb',
      textAlign: 'center',
    },
    alertSuccess: {
      backgroundColor: '#d4edda',
      color: '#155724',
      padding: '16px',
      borderRadius: '8px',
      marginBottom: '24px',
      border: '1px solid #c3e6cb',
      textAlign: 'center',
    },
    mainGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
      gap: '24px',
      marginBottom: '32px',
    },
    loadingContainer: {
      textAlign: 'center',
      padding: '60px 20px',
    },
  };

  // ==========================================
  // RENDER
  // ==========================================
  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <h1 style={styles.title}>üí≥ Simulador de Cr√©dito</h1>
        <p style={styles.subtitle}>
          Calcula tu cuota y descubre las mejores condiciones para tu cr√©dito
        </p>
      </div>

      {/* Alertas */}
      {error && (
        <div style={styles.alertError}>
          ‚ùå {error}
        </div>
      )}

      {success && (
        <div style={styles.alertSuccess}>
          {success}
        </div>
      )}

      {/* Loading de configuraci√≥n */}
      {loadingConfig && (
        <div style={styles.loadingContainer}>
          <p>‚è≥ Cargando configuraci√≥n...</p>
        </div>
      )}

      {/* Contenido principal */}
      {!loadingConfig && config && (
        <>
          {/* Grid Principal: Formulario + Vista Previa */}
          <div style={styles.mainGrid}>
            {/* Formulario */}
            <SimulacionForm
              monto={monto}
              setMonto={setMonto}
              plazo={plazo}
              setPlazo={setPlazo}
              config={config}
              loading={loading}
              onSubmit={handleSimular}
            />

            {/* Vista Previa */}
            <VistaPrevia
              vistaPrevia={vistaPrevia}
              onToggleAmortizacion={toggleAmortizacion}
              mostrarAmortizacion={mostrarAmortizacion}
            />
          </div>

          {/* Tabla de Amortizaci√≥n */}
          <TablaAmortizacion
            tabla={tablaAmortizacion}
            visible={mostrarAmortizacion}
          />

          {/* Historial de Simulaciones */}
          <HistorialSimulaciones
            historial={historial}
            loading={loadingHistorial}
            mostrarTodo={mostrarTodoHistorial}
            onEliminar={handleEliminar}
            onVerMas={handleVerMas}
          />
        </>
      )}
    </div>
  );
}
